def OSA(source: str, target: str) -> int:
    m = len(source)
    n = len(target)
    dp = np.zeros((m + 1, n + 1), dtype=int)
    for i in range(m + 1):
        dp[i][0] = i
    for j in range(n + 1):
        dp[0][j] = j
    for i in range(1, m + 1):
        for j in range(1, n + 1):
            if source[i - 1] == target[j - 1]:
                cost = 0
            else:
                cost = 1
            dp[i][j] = min(dp[i - 1][j] + 1, dp[i][j - 1] + 1, dp[i - 1][j - 1] + cost, dp[i - 2][j - 2] + cost + 1 if i >= 2 and j >= 2 and (source[i - 2] == target[j - 2]) else float('inf'))
    return dp[m][n]

