def gaussian_elimination(A, b):
    A = np.array(A)
    b = np.array(b)
    n = len(A)
    for i in range(n):
        max_row_index = i
        for j in range(i + 1, n):
            if abs(A[j][i]) > abs(A[max_row_index][i]):
                max_row_index = j
        A[[i, max_row_index]] = A[[max_row_index, i]]
        b[[i, max_row_index]] = b[[max_row_index, i]]
        for j in range(i + 1, n):
