def compute_tf_idf(corpus, query):
    if not corpus:
        raise ValueError('Corpus cannot be empty.')
    vocabulary = set((word for doc in corpus for word in doc))
    tf_dict = {word: {} for word in vocabulary}
    for (i, doc) in enumerate(corpus):
        doc_length = len(doc)
        for word in doc:
            if word in vocabulary:
                tf_dict[word][i] = doc.count(word) / doc_length
    idf_dict = {}
    n_docs = len(corpus)
    for word in vocabulary:
        df = sum((1 for doc in corpus if word in doc))
        if df == 0:
            idf_dict[word] = 1 / (n_docs + 1)
        else:
            idf_dict[word] = np.log(n_docs / df) + 1
    tfidf_scores = []
    for word in query:
        if word not in vocabulary:
            tfidf_scores.append([0.0] * len(corpus))
        else:
            tfidf_word_scores = []
            for (i, doc) in enumerate(corpus):
                if word in doc:
                    tfidf_word_scores.append(tf_dict[word][i] * idf_dict[word])
                else:
                    tfidf_word_scores.append(0.0)
            tfidf_scores.append(np.round(tfidf_word_scores, decimals=4).tolist())
    return tfidf_scores
