        for (i, token_id) in enumerate(context):
            pos_embedding[0][i] = i
        inputs = np.concatenate([np.expand_dims(params['wte'][token_id], axis=0), pos_embedding], axis=-1)
        hidden_states = np.dot(inputs, params['wte'].T)
        intermediate = np.maximum(hidden_states + np.random.normal(size=hidden_states.shape), 0)
        output = np.dot(intermediate, params['wte'].T)
        probs = np.exp(output) / np.sum(np.exp(output))
        next_token_id = np.random.choice(len(probs), p=probs)
        context.append(next_token_id)
        generated_text.append(encoder.decode([next_token_id]))
    generated_text = ''.join(generated_text)
    return generated_text
