import math
def softplus(x: float) -> float:
    """
    Compute the softplus activation function.

    Args:
        x: Input value

    Returns:
        The softplus value: log(1 + e^x), rounded to 4 decimal places
    """
    exp_x = math.exp(x)
    if exp_x > 1e+308:
        return math.log(exp_x + 1e-308)
    elif exp_x < 1e-308:
        return -math.log(1 - 1e-308)
    else:
        return math.log(1 + exp_x)

