import numpy as np
from collections import Counter
def meteor_score(reference, candidate, alpha=0.9, beta=3, gamma=0.5):
    ref_tokens = reference.split()
    cand_tokens = candidate.split()
    ref_counter = Counter(ref_tokens)
    cand_counter = Counter(cand_tokens)
    total_ref_words = len(ref_tokens)
    total_cand_words = len(cand_tokens)
    common_words = set(ref_counter.keys()) & set(cand_counter.keys())
    num_unigrams = sum((min(ref_counter[word], cand_counter[word]) for word in common_words))
    if total_cand_words == 0:
        precision = 0
    else:
        precision = num_unigrams / total_cand_words
    if total_ref_words == 0:
        recall = 0
    else:
        recall = num_unigrams / total_ref_words
    if precision + recall == 0:
        f_measure = 0
    else:
        f_measure = (1 + alpha ** 2) * (precision * recall) / (alpha ** 2 * precision + recall)
    frag_penalty = min(1, max(0, (total_cand_words - total_ref_words) / total_ref_words))
    meteor_score = beta * f_measure + gamma * frag_penalty
    return round(meteor_score, 3)

def min_max(x: list[int]) -> list[float]:
    if not x:
        return []
    min_val = min(x)
    max_val = max(x)
    if min_val == max_val:
        return [0.0] * len(x)
    normalized_x = [(value - min_val) / (max_val - min_val) for value in x]
    normalized_x_rounded = [round(value, 4) for value in normalized_x]
    return normalized_x_rounded

import numpy as np
def bhattacharyya_distance(p: list[float], q: list[float]) -> float:
    if len(p) != len(q):
        return 0.0
    prod = [a * b for (a, b) in zip(p, q)]
    sum_prod = sum(prod)
    bc_dist = np.sqrt(sum_prod)
    return round(bc_dist, 4)
