        for j in range(n):
            if grid[i][j] == 'F':
                fire_positions.add((i, j))
            elif grid[i][j] == '#':
                fire_positions.add((i, j))
    fire_queue = deque(fire_positions)
    agent_queue = deque([agent_position])
    visited = set(fire_positions)
    minutes = 0
