def knn_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int, metric: str='euclidean') -> np.ndarray:
    """Implement your code here."""
    y_pred = np.zeros(len(X_test), dtype=y_train.dtype)

    def euclidean_distance(x1, x2):
        return np.sqrt(np.sum((x1 - x2) ** 2))

    def manhattan_distance(x1, x2):
        return np.sum(np.abs(x1 - x2))

    def cosine_distance(x1, x2):
        return 1 - np.dot(x1, x2) / (np.linalg.norm(x1) * np.linalg.norm(x2))
    distance_func = {'euclidean': euclidean_distance, 'manhattan': manhattan_distance, 'cosine': cosine_distance}.get(metric, euclidean_distance)
    for (i, x_test) in enumerate(X_test):
        distances = np.array([distance_func(x_test, x_train) for x_train in X_train])
        nearest_indices = np.argsort(distances)[:k]
        nearest_labels = y_train[nearest_indices]
        (unique_labels, counts) = np.unique(nearest_labels, return_counts=True)
        if len(unique_labels) == 1:
            y_pred[i] = unique_labels[0]
        else:
            y_pred[i] = unique_labels[np.argmax(counts)]
