def gini_impurity(y):
    """Calculate the Gini impurity of a set of labels."""
    (_, counts) = np.unique(y, return_counts=True)
    probabilities = counts / len(y)
    return 1 - np.sum(probabilities ** 2)
def split_data(X, y, feature, threshold):
    """Split the data based on a feature and threshold."""
    left_mask = X[:, feature] <= threshold
    right_mask = X[:, feature] > threshold
    return (X[left_mask], y[left_mask], X[right_mask], y[right_mask])
def best_split(X, y):
    """Find the best feature and threshold to split the data."""
    best_feature = None
    best_threshold = None
    best_gain = 0
    parent_gini = gini_impurity(y)
    for feature in range(X.shape[1]):
        sorted_indices = np.argsort(X[:, feature])
        thresholds = (X[sorted_indices, feature][:-1] + X[sorted_indices, feature][1:]) / 2
        for threshold in thresholds:
            (left_X, left_y, right_X, right_y) = split_data(X, y, feature, threshold)
            if len(left_y) == 0 or len(right_y) == 0:
                continue
            left_gini = gini_impurity(left_y)
            right_gini = gini_impurity(right_y)
            gain = parent_gini - len(left_y) / len(y) * left_gini - len(right_y) / len(y) * right_gini
            if gain > best_gain:
                best_gain = gain
                best_feature = feature
                best_threshold = threshold
    return (best_feature, best_threshold, best_gain)
def build_tree(X, y, max_depth, current_depth=0):
    """Build the decision tree recursively."""
    if len(np.unique(y)) == 1 or (max_depth is not None and current_depth >= max_depth):
        return {'type': 'leaf', 'label': np.argmax(np.bincount(y))}
    (feature, threshold, gain) = best_split(X, y)
    if gain == 0:
        return {'type': 'leaf', 'label': np.argmax(np.bincount(y))}
    (left_X, left_y, right_X, right_y) = split_data(X, y, feature, threshold)
    left_tree = build_tree(left_X, left_y, max_depth, current_depth + 1)
    right_tree = build_tree(right_X, right_y, max_depth, current_depth + 1)
    return {'type': 'node', 'feature': feature, 'threshold': threshold, 'left': left_tree, 'right': right_tree}
def predict(tree, x):
    """Predict the label for a single sample using the decision tree."""
    if tree['type'] == 'leaf':
        return tree['label']
    elif x[tree['feature']] <= tree['threshold']:
        return predict(tree['left'], x)
    else:
        return predict(tree['right'], x)
