import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def euclidean(x, y):
        x = np.array(x, dtype=float)
        y = np.array(y, dtype=float)
        if len(x) != len(y) or len(x) == 0:
            return -1
        return round(np.sqrt(np.sum((x - y) ** 2)), 4)

    def manhattan(x, y):
        x = np.array(x, dtype=float)
        y = np.array(y, dtype=float)
        if len(x) != len(y) or len(x) == 0:
            return -1
        return round(np.sum(np.abs(x - y)), 4)

    def chebyshev(x, y):
        x = np.array(x, dtype=float)
        y = np.array(y, dtype=float)
        if len(x) != len(y) or len(x) == 0:
            return -1
        return round(np.max(np.abs(x - y)), 4)

    def cosine(x, y):
        x = np.array(x, dtype=float)
        y = np.array(y, dtype=float)
        if len(x) != len(y) or len(x) == 0:
            return -1
        if np.linalg.norm(x) == 0 or np.linalg.norm(y) == 0:
            return -1
        return round(1 - np.dot(x, y) / (np.linalg.norm(x) * np.linalg.norm(y)), 4)
    if name == 'euclidean':
        return euclidean
    elif name == 'manhattan':
        return manhattan
    elif name == 'chebyshev':
        return chebyshev
    elif name == 'cosine':
        return cosine
    else:
        return lambda x, y: -1

def calculate_hand_value(hand: list[int]) -> int:
    """Calculate the value of a hand, considering the rules for usable aces."""
    total = sum(hand)
    aces = hand.count(1)
    if aces > 0 and total + 10 <= 21:
        total += 10
    return total
def is_bust(hand: list[int]) -> bool:
    """Check if a hand is a bust (score > 21)."""
    return calculate_hand_value(hand) > 21
def is_natural_blackjack(hand: list[int]) -> bool:
    """Check if a hand is a natural blackjack (two cards, one Ace and one 10-value card)."""
    return len(hand) == 2 and 1 in hand and (sum(hand) == 11)
def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack."""
    player_value = calculate_hand_value(player)
    dealer_value = calculate_hand_value(dealer)
    if is_bust(player):
        return -1.0
    if is_bust(dealer):
        return 1.0
    if player_value == dealer_value:
        return 0.0
    if player_value > dealer_value:
        if is_natural_blackjack(player):
            return 1.5
        return 1.0
    return -1.0

