def spectral_clustering(X: np.ndarray, k: int) -> list[int]:
    """Cluster data using Spectral Clustering.

    Args:
        X: 2-D NumPy array where each row is a data sample.
        k: Desired number of clusters.

    Returns:
        List of length n_samples containing an integer cluster label for each sample.
    """
    (n, d) = X.shape
    W = np.zeros((n, n))
    for i in range(n):
        for j in range(n):
            if i != j:
                W[i, j] = 1 / (1 + np.linalg.norm(X[i] - X[j]))
    D = np.diag(np.sum(W, axis=1))
    L = D - W
    (eigenvalues, eigenvectors) = np.linalg.eigh(L)
    E = eigenvectors[:, :k]
    centroids = E[:k]
    labels = np.zeros(n, dtype=int)
    max_iterations = 100
    for _ in range(max_iterations):
        new_labels = np.zeros(n, dtype=int)
        for i in range(n):
            distances = np.linalg.norm(E[i] - centroids, axis=1)
            new_labels[i] = np.argmin(distances)
        if np.array_equal(labels, new_labels):
            break
        labels = new_labels
        for i in range(k):
            if np.sum(labels == i) == 0:
                empty_indices = np.where(labels == -1)[0]
                if len(empty_indices) > 0:
                    centroids[i] = E[empty_indices[0]]
    final_labels = np.zeros(n, dtype=int)
    for i in range(k):
        cluster_indices = np.where(labels == i)[0]
        cluster_points = X[cluster_indices]
        centroid = np.mean(cluster_points, axis=0)
        for j in cluster_indices:
            final_labels[j] = np.argmin(np.linalg.norm(X[j] - centroids, axis=1))
    return final_labels.tolist()

