def selu(x, order: int=0):
    """Compute the SELU activation or its derivatives.

    Parameters
    ----------
    x : float | list | np.ndarray
        Input value(s). Can be a scalar, 1-D list/array, or multi-D list/array.
    order : int, default 0
        0 → SELU(x)
        1 → first derivative d(SELU)/dx
        2 → second derivative d²(SELU)/dx²

    Returns
    -------
    list | float
        A Python list (or scalar for scalar input) containing the element-wise
        result, rounded to 4 decimals.
    """
    alpha = 1.6732632423543772
    scale = 1.0507009873554805
    x = np.array(x)

    def elu(x, alpha):
        return np.where(x > 0, x, alpha * (np.exp(x) - 1))

    def selu_function(x, alpha, scale):
        return scale * elu(x, alpha)

    def selu_derivative(x, alpha, scale):
        return scale * np.where(x > 0, 1, alpha * np.exp(x))

    def selu_second_derivative(x, alpha, scale):
        return scale * np.where(x > 0, 0, alpha * np.exp(x))
    if order == 0:
        result = selu_function(x, alpha, scale)
    elif order == 1:
        result = selu_derivative(x, alpha, scale)
    elif order == 2:
        result = selu_second_derivative(x, alpha, scale)
    else:
        raise ValueError('Invalid order. Must be 0, 1, or 2.')
    return result.round(4).tolist()

