class DecisionTreeRegressor:

    def __init__(self, max_depth=None):
        self.max_depth = max_depth
        self.tree = None

    def fit(self, X, y):
        self.tree = self._build_tree(X, y, depth=0)

    def predict(self, X):
        return np.array([self._predict_single(x, self.tree) for x in X])

    def _build_tree(self, X, y, depth):
        if depth == self.max_depth or len(np.unique(y)) == 1 or len(X) == 0:
            return {'value': np.mean(y)}
        best_split = self._find_best_split(X, y)
        if best_split is None:
            return {'value': np.mean(y)}
        left_mask = X[:, best_split['feature']] <= best_split['threshold']
        right_mask = X[:, best_split['feature']] > best_split['threshold']
        left_tree = self._build_tree(X[left_mask], y[left_mask], depth + 1)
        right_tree = self._build_tree(X[right_mask], y[right_mask], depth + 1)
        return {'feature': best_split['feature'], 'threshold': best_split['threshold'], 'left': left_tree, 'right': right_tree}

    def _find_best_split(self, X, y):
        best_gain = -np.inf
        best_split = None
        for feature in range(X.shape[1]):
            thresholds = np.unique(X[:, feature])
            for threshold in thresholds:
                left_mask = X[:, feature] <= threshold
                right_mask = X[:, feature] > threshold
                if len(left_mask) == 0 or len(right_mask) == 0:
                    continue
                gain = self._gain(y, y[left_mask], y[right_mask])
                if gain > best_gain:
                    best_gain = gain
                    best_split = {'feature': feature, 'threshold': threshold}
        return best_split

    def _gain(self, parent, left, right):
        n = len(parent)
        n_left = len(left)
        n_right = len(right)
        gini_parent = self._gini(parent)
        gini_left = self._gini(left)
        gini_right = self._gini(right)
        return gini_parent - n_left / n * gini_left - n_right / n * gini_right

    def _gini(self, y):
        if len(y) == 0:
            return 0
        counts = np.bincount(y)
        total = np.sum(counts)
        return 1 - np.sum((counts / total) ** 2)

    def _predict_single(self, x, node):
        if 'value' in node:
            return node['value']
        if x[node['feature']] <= node['threshold']:
            return self._predict_single(x, node['left'])
        else:
            return self._predict_single(x, node['right'])

