import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    if epsilon == 0:
        return []
    np.random.seed(42)

    def build_tree(subsample):
        if len(subsample) <= 1:
            return None
        if len(subsample) == 2:
            return (subsample[0], subsample[1])
        depth = math.ceil(math.log2(len(subsample)))
        feature_index = np.random.randint(0, subsample.shape[1])
        feature_values = subsample[:, feature_index]
        threshold = np.random.uniform(np.min(feature_values), np.max(feature_values))
        left_indices = np.where(subsample[:, feature_index] < threshold)[0]
        right_indices = np.where(subsample[:, feature_index] >= threshold)[0]
        left_subsample = subsample[left_indices]
        right_subsample = subsample[right_indices]
        return (feature_index, threshold, build_tree(left_subsample), build_tree(right_subsample))

    def path_length(node, observation):
        if node is None:
            return 0
        if node[1] is None:
            return 1 + c(len(node[0]))
        if observation[node[0]] < node[1]:
            return 1 + path_length(node[2], observation)
        else:
            return 1 + path_length(node[3], observation)

    def c(n):
        if n == 1:
            return 0
        return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n
    trees = [build_tree(np.random.choice(data, sample_size, replace=False)) for _ in range(n_trees)]
    path_lengths = np.zeros(data.shape[0])
    for tree in trees:
        for (i, observation) in enumerate(data):
            path_lengths[i] += path_length(tree, observation)
    average_path_lengths = path_lengths / n_trees
    phi = 2 * math.log(data.shape[0] - 1) - 2 * (data.shape[0] - 1) / data.shape[0]
    scores = 2 ** (-average_path_lengths / phi)
    threshold = np.percentile(scores, 100 * (1 - epsilon))
    outliers = np.where(scores >= threshold)[0]
    return sorted(outliers)
