import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    (num_samples, num_features) = X_train.shape
    num_labels = len(np.unique(y_train))
    P_y = np.zeros(num_labels)
    for label in y_train:
        P_y[label] += 1
    P_y /= num_samples
    P_fy = defaultdict(lambda : defaultdict(lambda : defaultdict(int)))
    for i in range(num_samples):
        label = y_train[i]
        for feature_index in range(num_features):
            feature_value = X_train[i, feature_index]
            P_fy[feature_index][feature_value][label] += 1
    weights = np.zeros(num_features * num_labels)
    for iteration in range(n_iter):
        max_update = 0
        for feature_index in range(num_features):
            for feature_value in np.unique(X_train[:, feature_index]):
                for label in range(num_labels):
                    current_prob = 0
                    for j in range(num_features):
                        if j == feature_index:
                            current_prob += weights[j * num_labels + label]
                        else:
                            current_prob += weights[j * num_labels + label] * X_train[i, j]
                    current_prob = math.exp(current_prob)
                    current_prob /= sum((math.exp(weights[j * num_labels + label] * X_train[i, j]) for j in range(num_features)))
                    empirical_prob = P_fy[feature_index][feature_value][label] / (num_samples * P_y[label])
                    update = empirical_prob * (1 - current_prob)
                    weights[feature_index * num_labels + label] += update
                    max_update = max(max_update, abs(update))
        if max_update < epsilon:
            break
    predictions = []
    for i in range(X_test.shape[0]):
        scores = np.zeros(num_labels)
        for feature_index in range(num_features):
            for label in range(num_labels):
                scores[label] += weights[feature_index * num_labels + label] * X_test[i, feature_index]
        predictions.append(np.argmax(scores))
    return predictions

import numpy as np
def local_outlier_factor(data, k, epsilon=1.5):
    """Your task is to implement this function!"""
    n_samples = data.shape[0]
    distances = np.zeros((n_samples, n_samples))
    for i in range(n_samples):
        for j in range(i + 1, n_samples):
            distances[i, j] = np.linalg.norm(data[i] - data[j])
            distances[j, i] = distances[i, j]
    k_distances = np.partition(distances, k, axis=1)[:, k]
    k_nearest_indices = np.argsort(distances, axis=1)[:, :k]

    def reachability_distance(p, q, k_distances):
        return max(k_distances[q], np.linalg.norm(p - q))

    def local_reachability_density(p, k_nearest_indices, k_distances):
        return k / np.sum([reachability_distance(data[p], data[q], k_distances) for q in k_nearest_indices[p]])
    lof_scores = []
    for p in range(n_samples):
        lrd_p = local_reachability_density(p, k_nearest_indices, k_distances)
        lof_p = np.sum([local_reachability_density(q, k_nearest_indices, k_distances) for q in k_nearest_indices[p]]) / k / lrd_p
        lof_scores.append(np.round(lof_p, 4))
    outlier_indices = [i for (i, score) in enumerate(lof_scores) if score > epsilon]
    return (lof_scores, sorted(outlier_indices))
