from collections import Counter, defaultdict
def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """
    Discover every frequent item-set in transactions with FP-Growth.

    A transaction is represented by a list of items (strings). min_support is the minimum number of transactions an item-set has to appear in.
    The result must be deterministic:
        - Items inside each item-set are sorted lexicographically.
        - The outer list is sorted by (len(itemset), itemset).
    """
    item_counts = Counter()
    for trans in transactions:
        for item in trans:
            item_counts[item] += 1

    def build_tree(items):
        if not items:
            return {}
        root = {'name': '', 'support': 0}
        for item in items:
            if item not in root['items']:
                root['items'][item] = {'name': item, 'count': 1}
        for child in build_tree(items[:-1]):
            root['children'].append(child)
        return root

    def mine_tree(root, min_support):
        if min_support <= 0:
            return []
        current_count = root['support']
        if current_count >= min_support:
            return [root['name']]
        children = root['children']
        results = []
        for child in children:
            results.extend(mine_tree(child, min_support))
        return results
    item_sets = []
    for item in item_counts:
        if item_counts[item] >= min_support:
            item_set = sorted([item])
            item_sets.append(item_set)
    item_sets.sort(key=lambda x: (len(x), x))
    return item_sets
