import numpy as np
import numpy as np
def knn_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int, metric: str='euclidean') -> np.ndarray:
    """
    Implement the k-Nearest Neighbors (k-NN) classifier.

    Parameters:
    - X_train: Training data with labels in y_train
    - y_train: Labels for training data
    - X_test: Test data to predict
    - k: Number of neighbors to consider
    - metric: The distance metric to use ('euclidean', 'manhattan', or 'cosine')

    Returns:
    - A 1-D NumPy array containing the predicted labels.
    """
    if metric == 'euclidean':

        def compute_distance(samples, label):
            return np.linalg.norm(samples - label)
        distances = np.zeros((len(X_test), len(X_train)))
        for (i, test_sample) in enumerate(X_test):
            for (j, train_label) in enumerate(y_train):
                distance = compute_distance(test_sample, train_label)
                distances[i, j] = distance
    elif metric == 'manhattan':

        def compute_distance(samples, label):
            return np.sum(samples - label)
        distances = np.zeros((len(X_test), len(X_train)))
        for (i, test_sample) in enumerate(X_test):
            for (j, train_label) in enumerate(y_train):
                distance = compute_distance(test_sample, train_label)
                distances[i, j] = distance
    elif metric == 'cosine':

        def compute_distance(samples, label):
            cos_sim = np.dot(samples - label, samples - label) / (np.linalg.norm(samples - label) * np.linalg.norm(samples - label))
            return 1 - cos_sim
    else:
        distances = np.zeros((len(X_test), len(X_train)))
        for (i, test_sample) in enumerate(X_test):
            for (j, train_label) in enumerate(y_train):
                distance = np.linalg.norm(test_sample - train_label)
                distances[i, j] = distance
    predictions = []
    for (test_sample_index, test_sample) in enumerate(X_test):
        distances_for_neighbors = distances[test_sample_index, :]
        distances_for_neighbors.sort()
        selected_neighbors = distances_for_neighbors[:k]
        label = None
        for neighbor in selected_neighbors:
            label = y_train[neighbors.index(neighbor)]
        predictions.append(label)
    return np.array(predictions)
