def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    player_value = sum(player)
    dealer_value = sum(dealer)
    if len(player) == 2 and 1 in player and (10 in player):
        if player_value == 11:
            reward = 1.5
        else:
            reward = 1.0
    else:
        player_bust = player_value > 21
        dealer_bust = dealer_value > 21
        if player_bust:
            reward = -1.0
        elif dealer_bust:
            reward = +1.0
        elif player_value > dealer_value:
            reward = +1.0
        elif player_value < dealer_value:
            reward = -1.0
        else:
            reward = 0.0
    return reward
