from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: Tuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    cont_action = False
    cont_obs = False
    if tuple_action:
        if isinstance(env.action_space, Tuple):
            for sub_space in env.action_space.spaces:
                if not isinstance(sub_space, Box):
                    cont_action = True
                    break
        elif isinstance(env.action_space, Dict):
            for (key, sub_space) in env.action_space.items():
                if not isinstance(sub_space, Box):
                    cont_action = True
                    break
        elif isinstance(env.action_space, Box):
            cont_action = True
    elif isinstance(env.action_space, Box):
        cont_action = True
    if tuple_obs:
        if isinstance(env.observation_space, Tuple):
            for sub_space in env.observation_space.spaces:
                if not isinstance(sub_space, Box):
                    cont_obs = True
                    break
        elif isinstance(env.observation_space, Dict):
            for (key, sub_space) in env.observation_space.items():
                if not isinstance(sub_space, Box):
                    cont_obs = True
                    break
        elif isinstance(env.observation_space, Box):
            cont_obs = True
    elif isinstance(env.observation_space, Box):
        cont_obs = True
    return (cont_action, cont_obs)
