from collections import Counter
from collections import Counter
from typing import Iterable, Tuple, List, Set, Any
def statistic_key_word(data: List[Tuple[Any, Iterable[str]]], cut_off: int | None=None) -> Tuple[List[Tuple[str, float]], Set[str]]:
    counts = {}
    total_documents = len(data)
    for (label, words) in data:
        count = Counter(words)
        for (word, freq) in count.items():
            counts[word] = freq
    word_freqs = []
    for (word, freq) in counts.items():
        ratio = freq / total_documents
        word_freqs.append((word, round(ratio, 4)))
    sorted_words = sorted(word_freqs, key=lambda x: -x[1])
    if cut_off is None:
        return (sorted_words, set(sorted_words))
    elif len(sorted_words) < cut_off:
        return (sorted_words, set(sorted_words))
    else:
        return (sorted_words[:cut_off], set(sorted_words[:cut_off]))
