def determinant_4x4(matrix: list[list[int | float]]) -> float:

    def determinant_3x3(mat: list[list[int | float]]) -> float:
        return mat[0][0] * (mat[1][1] * mat[2][2] - mat[1][2] * mat[2][1]) - mat[0][1] * (mat[1][0] * mat[2][2] - mat[1][2] * mat[2][0]) + mat[0][2] * (mat[1][0] * mat[2][1] - mat[1][1] * mat[2][0])

    def determinant(mat: list[list[int | float]]) -> float:
        if len(mat) == 1:
            return mat[0][0]
        if len(mat) == 2:
            return mat[0][0] * mat[1][1] - mat[0][1] * mat[1][0]
        det = 0.0
        for col in range(len(mat)):
            sign = 1 if col % 2 == 0 else -1
            minor = [[mat[row][c] for c in range(len(mat)) if c != col] for row in range(1, len(mat))]
            det += sign * mat[0][col] * determinant(minor)
        return det
    if len(matrix) != 4 or any((len(row) != 4 for row in matrix)):
        raise ValueError('Input must be a 4x4 matrix')
    return determinant(matrix)
