class Dense(Layer):

    def __init__(self, n_units, input_shape=None):
        self.layer_input = None
        self.input_shape = input_shape
        self.n_units = n_units
        self.trainable = True
        self.W = None
        self.w0 = None
        self.optimizer_W = None
        self.optimizer_w0 = None

    def initialize(self, optimizer):
        if self.input_shape is None:
            raise ValueError('Input shape must be specified for weight initialization.')
        input_dim = self.input_shape[0]
        limit = 1.0 / math.sqrt(input_dim)
        self.W = np.random.uniform(-limit, limit, size=(input_dim, self.n_units))
        self.w0 = np.zeros(shape=(self.n_units,))
        self.optimizer_W = copy.deepcopy(optimizer)
        self.optimizer_w0 = copy.deepcopy(optimizer)

    def parameters(self):
        return int(np.prod(self.W.shape) + np.prod(self.w0.shape))

    def forward_pass(self, X, training):
        self.layer_input = X
        Z = np.dot(X, self.W) + self.w0
        return Z

    def backward_pass(self, accum_grad):
        grad_input = np.dot(accum_grad, self.W.T)
        grad_W = np.dot(self.layer_input.T, accum_grad)
        grad_w0 = np.sum(accum_grad, axis=0)
        if self.trainable:
            self.W = self.optimizer_W.update(self.W, grad_W)
            self.w0 = self.optimizer_w0.update(self.w0, grad_w0)
        return grad_input

    def output_shape(self):
        return (self.n_units,)
