import numpy as np
def gauss_seidel(A, b, n, x_ini=None):
    A = np.array(A)
    b = np.array(b)
    size = A.shape[0]
    if x_ini is None:
        x = np.zeros(size)
    else:
        x = np.array(x_ini)
    for _ in range(n):
        for i in range(size):
            sum_term = 0
            for j in range(size):
                if j != i:
                    sum_term += A[i, j] * x[j]
            x[i] = (b[i] - sum_term) / A[i, i]
    return np.round(x, 4).tolist()
