def calculate_brightness(img):
    if not img:
        return -1
    row_length = len(img[0])
    for row in img:
        if len(row) != row_length:
            return -1
    for row in img:
        for pixel in row:
            if not 0 <= pixel <= 255:
                return -1
    total_brightness = sum((sum(row) for row in img))
    total_pixels = row_length * len(img)
    return round(total_brightness / total_pixels, 2)
assert calculate_brightness([]) == -1
