def create_row_hv(row, dim, random_seeds):

    def generate_hv(seed=None):
        np.random.seed(seed)
        return np.random.choice([-1, 1], size=dim)

    def bind(hv1, hv2):
        return hv1 * hv2

    def bundle(hvs):
        return np.sign(np.sum(hvs, axis=0))
    feature_hvs = []
    for (feature_name, feature_value) in row.items():
        name_seed = random_seeds.get(feature_name, None)
        name_hv = generate_hv(name_seed)
        value_seed = random_seeds.get(feature_name, None)
        value_hv = generate_hv(value_seed)
        bound_hv = bind(name_hv, value_hv)
        feature_hvs.append(bound_hv)
    composite_hv = bundle(feature_hvs)
    return composite_hv.tolist()
