def descriptive_statistics(data):
    data = np.array(data)
    mean = np.mean(data)
    median = np.median(data)
    try:
        mode = float(stats.mode(data, keepdims=False).mode)
    except IndexError:
        mode = None
    variance = np.var(data, ddof=1)
    standard_deviation = np.std(data, ddof=1)
    percentiles = np.percentile(data, [25, 50, 75])
    twenty_fifth_percentile = percentiles[0]
    fiftyth_percentile = percentiles[1]
    seventy_fifth_percentile = percentiles[2]
    interquartile_range = seventy_fifth_percentile - twenty_fifth_percentile
    result = {'mean': round(mean, 4), 'median': round(median, 4), 'mode': mode, 'variance': round(variance, 4), 'standard_deviation': round(standard_deviation, 4), '25th_percentile': round(twenty_fifth_percentile, 4), '50th_percentile': round(fiftyth_percentile, 4), '75th_percentile': round(seventy_fifth_percentile, 4), 'interquartile_range': round(interquartile_range, 4)}
    return result
