from typing import List, Dict, Tuple, Hashable
def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    N = len(transactions)
    if N == 0:
        return {}
    threshold = math.ceil(minsup * N)
    transaction_sets = [set(transaction) for transaction in transactions]
    result = {}
    item_counts = Counter()
    for t_set in transaction_sets:
        item_counts.update(t_set)
    level_1 = {}
    for item in item_counts:
        if item_counts[item] >= threshold:
            level_1[item,] = item_counts[item]
    result.update(level_1)
    if not level_1:
        return result
    previous_frequent = level_1
    while True:
        itemset_size = len(next(iter(previous_frequent.keys())))
        current_level = itemset_size + 1
        items = set()
        for itemset in previous_frequent:
            items.update(itemset)
        items = sorted(items)
        candidates = combinations(items, current_level)
        valid_candidates = []
        for candidate in candidates:
            is_valid = True
            for subset in combinations(candidate, current_level - 1):
                subset_tuple = tuple(sorted(subset))
                if subset_tuple not in previous_frequent:
                    is_valid = False
                    break
            if is_valid:
                valid_candidates.append(tuple(sorted(candidate)))
        new_frequent = {}
        for candidate in valid_candidates:
            support = 0
            for t_set in transaction_sets:
                if set(candidate).issubset(t_set):
                    support += 1
            if support >= threshold:
                new_frequent[candidate] = support
        if not new_frequent:
            break
        result.update(new_frequent)
        previous_frequent = new_frequent
    return result
