def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if task_type == 'classification':

        def traverse_classification(node):
            if hasattr(node, 'y'):
                return node.y
            feature_value = features[node.label]
            for child in node.child:
                if child.x == feature_value:
                    return traverse_classification(child)
            return traverse_classification(node.child[1])
        return traverse_classification(root)
    elif task_type == 'regression':

        def traverse_regression(node):
            if hasattr(node, 'y'):
                return node.y
            feature_value = features[node.label]
            if feature_value <= node.s:
                return traverse_regression(node.child[0])
            else:
                return traverse_regression(node.child[1])
        return traverse_regression(root)
