def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    N = len(transactions)
    if N == 0:
        return {}
    threshold = math.ceil(minsup * N)
    transaction_sets = [set(transaction) for transaction in transactions]
    result = {}
    item_counts = Counter()
    for t_set in transaction_sets:
        item_counts.update(t_set)
    level_1 = {}
    for item in item_counts:
        if item_counts[item] >= threshold:
            level_1[item,] = item_counts[item]
    result.update(level_1)
    if not level_1:
        return result
    previous_frequent = level_1
    while True:
        itemset_size = len(next(iter(previous_frequent.keys())))
        current_level = itemset_size + 1
        items = set()
        for itemset in previous_frequent:
            items.update(itemset)
        items = sorted(items)
        candidates = combinations(items, current_level)
        valid_candidates = []
        for candidate in candidates:
            is_valid = True
            for subset in combinations(candidate, current_level - 1):
                subset_tuple = tuple(sorted(subset))
                if subset_tuple not in previous_frequent:
                    is_valid = False
                    break
            if is_valid:
                valid_candidates.append(tuple(sorted(candidate)))
        new_frequent = {}
        for candidate in valid_candidates:
            support = 0
            for t_set in transaction_sets:
                if set(candidate).issubset(t_set):
                    support += 1
            if support >= threshold:
                new_frequent[candidate] = support
        if not new_frequent:
            break
        result.update(new_frequent)
        previous_frequent = new_frequent
    return result

import numpy as np
def is_stochastic(X: list[list[float]] | 'np.ndarray') -> bool:
    """Return True if *X* is a row-stochastic matrix, otherwise False.

    A matrix is row-stochastic when every element is a probability (0 ≤ p ≤ 1)
    and each row sums to 1 (within a small numerical tolerance).

    Args:
        X: Matrix given as a list of lists or a NumPy array.

    Returns:
        bool: True if *X* is row-stochastic, False otherwise.
    """
    if isinstance(X, np.ndarray):
        if X.ndim != 2:
            return False
        if not np.all((X >= 0) & (X <= 1)):
            return False
        row_sums = X.sum(axis=1)
        if not np.allclose(row_sums, 1, atol=1e-08):
            return False
        return True
    else:
        if not isinstance(X, list):
            return False
        if not all((isinstance(row, list) for row in X)):
            return False
        if len(set((len(row) for row in X))) != 1:
            return False
        for row in X:
            for element in row:
                if not 0 <= element <= 1:
                    return False
        for row in X:
            if not np.allclose(sum(row), 1, atol=1e-08):
                return False
        return True

import numpy as np
def softplus(x, deriv: bool=False):
    """Implement the SoftPlus activation function and its derivative."""
    x = np.array(x)
    if deriv:
        result = 1 / (1 + np.exp(-x))
    else:
        mask = x >= 0
        result = np.zeros_like(x)
        result[mask] = x[mask] + np.log1p(np.exp(-x[mask]))
        result[~mask] = np.log1p(np.exp(x[~mask]))
    rounded_result = np.round(result, 4)
    if x.ndim == 0:
        return float(rounded_result.item())
    else:
        return rounded_result.tolist()

import numpy as np
def forward_algorithm(S: list[float], A: list[list[float]], B: list[list[float]], observations: list[int]) -> float:
    """Forward algorithm for Hidden Markov Models.

    Args:
        S (list[float]): Initial state probabilities.
        A (list[list[float]]): State–transition probabilities.
        B (list[list[float]]): Emission probabilities.
        observations (list[int]): Observation index sequence.

    Returns:
        float: Sequence likelihood rounded to 4 decimals, or −1 on invalid input.
    """
    if not all((isinstance(x, float) and 0 <= x <= 1 for x in S)):
        return -1
    if not all((len(row) == len(S) and all((0 <= x <= 1 for x in row)) for row in A)):
        return -1
    if not all((len(row) == len(B[0]) and all((0 <= x <= 1 for x in row)) for row in B)):
        return -1
    if not isinstance(observations, list) or not all((isinstance(obs, int) for obs in observations)):
        return -1
    if len(observations) == 0:
        return -1
    n = len(S)
    m = len(B[0])
    if len(A) != n or any((len(row) != n for row in A)):
        return -1
    if len(B) != n or any((len(row) != m for row in B)):
        return -1
    for obs in observations:
        if not 0 <= obs < m:
            return -1
    S = np.array(S)
    A = np.array(A)
    B = np.array(B)
    T = len(observations)
    alpha = np.zeros((T, n))
    alpha[0, :] = S * B[:, observations[0]]
    for t in range(1, T):
        for j in range(n):
            alpha[t, j] = np.dot(alpha[t - 1, :], A[:, j]) * B[j, observations[t]]
    likelihood = np.sum(alpha[-1, :])
    return round(likelihood, 4)

from collections import Counter, defaultdict
def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth.

    A *transaction* is represented by a list of items (strings).  `min_support`
    is the minimum number of transactions an item-set has to appear in.  The
    function returns **all** frequent item-sets where
        support(itemset) >= min_support.

    The result must be deterministic:
      • Inside each item-set the items have to be sorted alphabetically.
      • The outer list has to be sorted by `(len(itemset), itemset)`.
    If *transactions* is empty or no item-set meets the threshold return an
    empty list.
    """
    if not transactions:
        return []
    item_counts = Counter()
    for transaction in transactions:
        item_counts.update(transaction)
    frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_support}
    if not frequent_items:
        return []
    sorted_items = sorted(frequent_items.keys())
    item_index = {item: idx for (idx, item) in enumerate(sorted_items)}

    class FPTreeNode:

        def __init__(self, item, count, parent=None):
            self.item = item
            self.count = count
            self.parent = parent
            self.children = {}
            self.transaction_ids = set()
    root = FPTreeNode('null', 0)
    header_table = defaultdict(list)
    for (tid, transaction) in enumerate(transactions):
        filtered_transaction = [item for item in transaction if item in frequent_items]
        if not filtered_transaction:
            continue
        filtered_transaction.sort(key=lambda x: -frequent_items[x])
        current_node = root
        for item in filtered_transaction:
            if item not in current_node.children:
                new_node = FPTreeNode(item, 1, current_node)
                current_node.children[item] = new_node
                header_table[item].append(new_node)
            else:
                current_node.children[item].count += 1
            current_node = current_node.children[item]
            current_node.transaction_ids.add(tid)

    def mine_tree(node, prefix, header_table, min_support, frequent_itemsets):
        conditional_pattern_base = []
        for (item, nodes) in header_table.items():
            for node in nodes:
                if node.parent is not None:
                    path = []
                    current = node.parent
                    while current.item != 'null':
                        path.append(current.item)
                        current = current.parent
                    path.reverse()
                    conditional_pattern_base.append((path, node.count))
        conditional_header_table = defaultdict(list)
        for (path, count) in conditional_pattern_base:
            for item in path:
                conditional_header_table[item].append(count)
        pattern_base = []
        for (path, count) in conditional_pattern_base:
            if path:
                pattern_base.append((path, count))
        if not pattern_base:
            return
        sorted_items = sorted(conditional_header_table.keys(), key=lambda x: -sum(conditional_header_table[x]))
        for item in sorted_items:
            item_support = sum(conditional_header_table[item])
            if item_support >= min_support:
                new_itemset = prefix + [item]
                new_itemset.sort()
                frequent_itemsets.append(new_itemset)
                conditional_tree = build_conditional_tree(pattern_base, item, min_support)
                mine_tree(conditional_tree, new_itemset, conditional_header_table, min_support, frequent_itemsets)

    def build_conditional_tree(pattern_base, item, min_support):
        root = FPTreeNode('null', 0)
        header_table = defaultdict(list)
        for (path, count) in pattern_base:
            if item in path:
                index = path.index(item)
                sub_path = path[:index]
                current_node = root
                for p in sub_path:
                    if p not in current_node.children:
                        new_node = FPTreeNode(p, 0, current_node)
                        current_node.children[p] = new_node
                        header_table[p].append(new_node)
                    current_node.children[p].count += count
                    current_node = current_node.children[p]
        return root
    frequent_itemsets = []
    mine_tree(root, [], header_table, min_support, frequent_itemsets)
    unique_itemsets = []
    seen = set()
    for itemset in frequent_itemsets:
        itemset_sorted = tuple(sorted(itemset))
        if itemset_sorted not in seen:
            seen.add(itemset_sorted)
            unique_itemsets.append(itemset_sorted)
    unique_itemsets.sort(key=lambda x: (len(x), x))
    return [list(itemset) for itemset in unique_itemsets]

