import math
def escape_fire_maze(grid: list[str]) -> int:
    n = len(grid)
    if n == 0:
        return -1
    if grid[0][0] == 'F' or grid[n - 1][n - 1] == 'F':
        return -1
    fire_time = [[math.inf] * n for _ in range(n)]
    q = deque()
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_time[i][j] = 0
                q.append((i, j))
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    while q:
        (x, y) = q.popleft()
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n and (grid[nx][ny] != '#') and (fire_time[nx][ny] == math.inf):
                fire_time[nx][ny] = fire_time[x][y] + 1
                q.append((nx, ny))
    agent_time = [[math.inf] * n for _ in range(n)]
    agent_q = deque()
    agent_q.append((0, 0))
    agent_time[0][0] = 0
    while agent_q:
        (x, y) = agent_q.popleft()
        current_time = agent_time[x][y]
        if x == n - 1 and y == n - 1:
            return current_time
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n and (grid[nx][ny] != '#'):
                if fire_time[nx][ny] > current_time + 1 and agent_time[nx][ny] > current_time + 1:
                    agent_time[nx][ny] = current_time + 1
                    agent_q.append((nx, ny))
    return -1

import numpy as np
def adaboost_predict(X_train: list[list[int | float]], y_train: list[int], X_test: list[list[int | float]], n_estimators: int=10) -> list[int]:
    """Train AdaBoost with decision stumps and predict on test data."""
    y_converted = [1 if y == 1 else -1 for y in y_train]
    n_samples = len(y_train)
    n_features = len(X_train[0]) if n_samples > 0 else 0
    sample_weights = [1.0 / n_samples] * n_samples
    stumps = []
    alphas = []
    for _ in range(n_estimators):
        best_error = float('inf')
        best_feature = -1
        best_threshold = None
        best_polarity = 1
        for feature_idx in range(n_features):
            feature_values = [x[feature_idx] for x in X_train]
            unique_vals = sorted(list(set(feature_values)))
            for threshold in unique_vals:
                for polarity in [1, -1]:
                    error = 0.0
                    for i in range(n_samples):
                        x_val = X_train[i][feature_idx]
                        if (x_val < threshold) == (polarity == 1):
                            h = 1
                        else:
                            h = -1
                        if h != y_converted[i]:
                            error += sample_weights[i]
                    if error < best_error:
                        best_error = error
                        best_feature = feature_idx
                        best_threshold = threshold
                        best_polarity = polarity
                    elif error == best_error:
                        if feature_idx < best_feature:
                            best_feature = feature_idx
                            best_threshold = threshold
                            best_polarity = polarity
                        elif feature_idx == best_feature:
                            if threshold < best_threshold:
                                best_threshold = threshold
                                best_polarity = polarity
                            elif threshold == best_threshold:
                                if polarity == 1 and best_polarity == -1:
                                    best_polarity = polarity
        if best_error == 0:
            break
        alpha = 0.5 * np.log((1 - best_error) / (best_error + 1e-10))
        stumps.append((best_feature, best_threshold, best_polarity))
        alphas.append(alpha)
        for i in range(n_samples):
            x_val = X_train[i][best_feature]
            if (x_val < best_threshold) == (best_polarity == 1):
                h = 1
            else:
                h = -1
            sample_weights[i] *= np.exp(-alpha * y_converted[i] * h)
        total_weight = sum(sample_weights)
        if total_weight > 0:
            sample_weights = [w / total_weight for w in sample_weights]
    predictions = []
    for x in X_test:
        score = 0.0
        for i in range(len(stumps)):
            (feature_idx, threshold, polarity) = stumps[i]
            alpha = alphas[i]
            x_val = x[feature_idx]
            if (x_val < threshold) == (polarity == 1):
                h = 1
            else:
                h = -1
            score += alpha * h
        predictions.append(1 if score >= 0 else 0)
    return predictions

import numpy as np
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test.

    Parameters
    ----------
    X_train : np.ndarray
        Training feature matrix of shape (n_samples, n_features).
    y_train : np.ndarray
        Integer class labels for the training data, shape (n_samples,).
    X_test : np.ndarray
        Feature matrix to classify, shape (m_samples, n_features).
    max_depth : int | None, optional
        Maximum allowed depth of the tree. If None the depth is unlimited.

    Returns
    -------
    list[int]
        Predicted class label for each row in X_test.
    """

    def gini(y):
        if len(y) == 0:
            return 0.0
        counts = np.bincount(y)
        probabilities = counts / len(y)
        return 1 - np.sum(probabilities ** 2)

    def majority_class(y):
        counts = np.bincount(y)
        return np.argmax(counts)

    def find_best_split(X, y):
        parent_gini = gini(y)
        best_gain = 0.0
        best_feature = None
        best_threshold = None
        n_features = X.shape[1]
        for feature in range(n_features):
            sorted_indices = np.argsort(X[:, feature])
            sorted_X = X[sorted_indices]
            sorted_y = y[sorted_indices]
            for i in range(len(sorted_X) - 1):
                if sorted_X[i, feature] != sorted_X[i + 1, feature]:
                    threshold = (sorted_X[i, feature] + sorted_X[i + 1, feature]) / 2
                    left_mask = X[:, feature] <= threshold
                    left_y = y[left_mask]
                    right_y = y[~left_mask]
                    if len(left_y) == 0 or len(right_y) == 0:
                        continue
                    gini_left = gini(left_y)
                    gini_right = gini(right_y)
                    weight_left = len(left_y) / len(y)
                    weight_right = len(right_y) / len(y)
                    children_gini = weight_left * gini_left + weight_right * gini_right
                    gain = parent_gini - children_gini
                    if gain > best_gain:
                        best_gain = gain
                        best_feature = feature
                        best_threshold = threshold
        if best_gain > 0:
            return (best_feature, best_threshold)
        else:
            return (None, None)

    def build_tree(X, y, depth):
        if len(set(y)) == 1:
            return {'value': y[0]}
        if max_depth is not None and depth >= max_depth:
            return {'value': majority_class(y)}
        (best_feature, best_threshold) = find_best_split(X, y)
        if best_feature is None:
            return {'value': majority_class(y)}
        left_mask = X[:, best_feature] <= best_threshold
        left_X = X[left_mask]
        left_y = y[left_mask]
        right_X = X[~left_mask]
        right_y = y[~left_mask]
        left_subtree = build_tree(left_X, left_y, depth + 1)
        right_subtree = build_tree(right_X, right_y, depth + 1)
        return {'feature': best_feature, 'threshold': best_threshold, 'left': left_subtree, 'right': right_subtree}

    def predict_sample(sample, node):
        if 'value' in node:
            return node['value']
        if sample[node['feature']] <= node['threshold']:
            return predict_sample(sample, node['left'])
        else:
            return predict_sample(sample, node['right'])
    tree = build_tree(X_train, y_train, 0)
    predictions = [predict_sample(x, tree) for x in X_test]
    return predictions

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function."""
    if name not in ['euclidean', 'manhattan', 'chebyshev', 'cosine']:

        def invalid_func(x, y):
            return -1
        return invalid_func

    def d(x, y):
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return -1
        try:
            x_arr = np.array(x, dtype=float)
            y_arr = np.array(y, dtype=float)
        except:
            return -1
        if x_arr.ndim != 1 or y_arr.ndim != 1:
            return -1
        if x_arr.size == 0 or y_arr.size == 0:
            return -1
        if x_arr.shape[0] != y_arr.shape[0]:
            return -1
        if name == 'cosine':
            norm_x = np.linalg.norm(x_arr)
            norm_y = np.linalg.norm(y_arr)
            if norm_x == 0 or norm_y == 0:
                return -1
        if name == 'euclidean':
            dist = np.linalg.norm(x_arr - y_arr)
        elif name == 'manhattan':
            dist = np.sum(np.abs(x_arr - y_arr))
        elif name == 'chebyshev':
            dist = np.max(np.abs(x_arr - y_arr))
        elif name == 'cosine':
            dot = np.dot(x_arr, y_arr)
            norm_x = np.linalg.norm(x_arr)
            norm_y = np.linalg.norm(y_arr)
            dist = 1 - dot / (norm_x * norm_y)
        else:
            return -1
        return round(dist, 4)
    return d

