def pam_clustering(X: np.ndarray, k: int) -> list[int]:
    (n, d) = X.shape
    medoids = list(range(k))

    def compute_cost(medoids_list):
        total = 0.0
        for i in range(n):
            x = X[i]
            min_dist = float('inf')
            for m in medoids_list:
                dist = np.linalg.norm(x - X[m])
                if dist < min_dist:
                    min_dist = dist
            total += min_dist
        return total
    current_cost = compute_cost(medoids)
    while True:
        best_swap = None
        best_new_cost = current_cost
        current_medoids_set = set(medoids)
        non_medoids = [o for o in range(n) if o not in current_medoids_set]
        for m in medoids:
            for o in non_medoids:
                new_medoids = [x for x in medoids if x != m] + [o]
                new_cost = compute_cost(new_medoids)
                if new_cost < best_new_cost:
                    best_new_cost = new_cost
                    best_swap = (m, o)
        if best_new_cost < current_cost:
            (m, o) = best_swap
            medoids = [x for x in medoids if x != m] + [o]
            current_cost = best_new_cost
        else:
            break
    labels = []
    for i in range(n):
        x = X[i]
        min_dist = float('inf')
        closest_medoid_index = 0
        for (idx, m) in enumerate(medoids):
            dist = np.linalg.norm(x - X[m])
            if dist < min_dist:
                min_dist = dist
                closest_medoid_index = idx
        labels.append(closest_medoid_index)
    return labels

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def calculate_score(hand):
        score = sum(hand)
        aces = hand.count(1)
        while aces > 0 and score + 10 <= 21:
            score += 10
            aces -= 1
        return score

    def is_natural_blackjack(hand):
        return len(hand) == 2 and {1, 10}.issubset(set(hand))
    player_score = calculate_score(player)
    dealer_score = calculate_score(dealer)
    if player_score > 21:
        return -1.0
    if dealer_score > 21:
        return 1.0
    if player_score > dealer_score:
        if is_natural_blackjack(player):
            return 1.5
        else:
            return 1.0
    elif player_score < dealer_score:
        return -1.0
    else:
        return 0.0

import numpy as np
def leaky_relu(z, a=0.01):
    """Apply the Leaky ReLU activation to every element in *z*.

    Args:
        z: A scalar, list (possibly nested) or ``numpy.ndarray`` of numbers.
        a: Optional float in [0,1) — the slope for negative inputs. Defaults to 0.01.

    Returns:
        A Python list with the same structure as *z* where each value has been transformed
        by the Leaky ReLU activation.
    """
    if isinstance(z, np.ndarray):
        return np.where(z >= 0, z, a * z).tolist()
    elif isinstance(z, list):
        return [leaky_relu(element, a) for element in z]
    else:
        return [z if z >= 0 else a * z]

