from typing import List
def ngram_log_prob(corpus: List[str], query: str, N: int) -> float:
    """Return the total log-probability of *query* under an N-gram MLE model.

    Parameters
    ----------
    corpus : list[str]
        A list of training sentences. Each sentence is a plain string; tokens
        are assumed to be separated by white-spaces.
    query : str
        The sentence whose probability should be calculated.
    N : int
        The order of the N-gram model (N ≥ 1).

    Returns
    -------
    float
        The sum of natural logarithms of the probabilities of every
        consecutive N-gram occurring in *query*, rounded to 4 decimal places.
        If any necessary N-gram is unseen in the corpus the function returns
        float('-inf').
    """
    if N < 1:
        raise ValueError('N must be at least 1')
    padded_corpus = []
    for sentence in corpus:
        tokens = sentence.split()
        if N > 1:
            padded_tokens = ['<bol>'] * (N - 1) + tokens + ['<eol>']
        else:
            padded_tokens = tokens
        padded_corpus.append(padded_tokens)
    ngrams = defaultdict(int)
    context_ngrams = defaultdict(int)
    for tokens in padded_corpus:
        for i in range(len(tokens) - N + 1):
            ngram = tuple(tokens[i:i + N])
            ngrams[ngram] += 1
            if N > 1:
                context = tuple(tokens[i:i + N - 1])
                context_ngrams[context] += 1
            else:
                pass
    total_tokens = sum((len(sentence.split()) for sentence in corpus))
    query_tokens = query.split()
    if N > 1:
        padded_query = ['<bol>'] * (N - 1) + query_tokens + ['<eol>']
    else:
        padded_query = query_tokens
    if len(padded_query) < N:
        return float('-inf')
    log_prob = 0.0
    for i in range(len(padded_query) - N + 1):
        current_ngram = tuple(padded_query[i:i + N])
        if N == 1:
            count = ngrams.get(current_ngram, 0)
            if count == 0:
                return float('-inf')
            prob = count / total_tokens
        else:
            context = tuple(padded_query[i:i + N - 1])
            count_ngram = ngrams.get(current_ngram, 0)
            count_context = context_ngrams.get(context, 0)
            if count_context == 0 or count_ngram == 0:
                return float('-inf')
            prob = count_ngram / count_context
        log_prob += math.log(prob)
    return round(log_prob, 4)

import numpy as np
def spectral_clustering(X: np.ndarray, k: int) -> list[int]:
    """Cluster data using Spectral Clustering.

    Args:
        X: 2-D NumPy array where each row is a data sample.
        k: Desired number of clusters.

    Returns:
        List of length n_samples containing an integer cluster label for each sample.
    """
    (n_samples, n_features) = X.shape
    W = np.zeros((n_samples, n_samples))
    for i in range(n_samples):
        for j in range(n_samples):
            if i != j:
                distance = np.linalg.norm(X[i] - X[j])
                W[i, j] = 1 / (1 + distance)
    D = np.diag(np.sum(W, axis=1))
    L = D - W
    (eigenvalues, eigenvectors) = np.linalg.eigh(L)
    indices = np.argsort(eigenvalues)
    E = eigenvectors[:, indices[:k]]
    centroids = E[:k, :]
    assignments = np.zeros(n_samples, dtype=int)

    def euclidean_distance(a, b):
        return np.sqrt(np.sum((a - b) ** 2))

    def assign_points(centroids, E):
        (n_clusters, n_dim) = centroids.shape
        n_points = E.shape[0]
        distances = np.zeros((n_points, n_clusters))
        for i in range(n_points):
            for j in range(n_clusters):
                distances[i, j] = euclidean_distance(E[i], centroids[j])
        return np.argmin(distances, axis=1)

    def update_centroids(assignments, E):
        n_clusters = k
        n_dim = E.shape[1]
        new_centroids = np.zeros((n_clusters, n_dim))
        counts = np.zeros(n_clusters)
        for i in range(n_clusters):
            cluster_points = E[assignments == i]
            if len(cluster_points) > 0:
                new_centroids[i] = np.mean(cluster_points, axis=0)
                counts[i] = len(cluster_points)
            else:
                idx = np.random.randint(n_samples)
                new_centroids[i] = E[idx]
                counts[i] = 1
        return new_centroids
    max_iter = 100
    for _ in range(max_iter):
        prev_assignments = assignments.copy()
        assignments = assign_points(centroids, E)
        if np.array_equal(prev_assignments, assignments):
            break
        centroids = update_centroids(assignments, E)
    return assignments.tolist()

import numpy as np
def mel_filterbank(N: int, n_filters: int=20, fs: int=44000, min_freq: int=0, max_freq: int | None=None, normalize: bool=True) -> list[list[float]]:
    """Build a Mel filterbank transformation matrix.

    The returned matrix has *n_filters* rows and *N//2 + 1* columns.  Each row
    is a triangular filter defined on the Mel scale.
    """
    if max_freq is None:
        max_freq = fs // 2

    def hz_to_mel(f):
        return 2595 * np.log10(1 + f / 700)

    def mel_to_hz(mel):
        return 700 * (10 ** (mel / 2595) - 1)
    mel_min = hz_to_mel(min_freq)
    mel_max = hz_to_mel(max_freq)
    mel_points = np.linspace(mel_min, mel_max, n_filters + 2)
    hz_points = mel_to_hz(mel_points)
    freqs = np.linspace(0, fs / 2, N // 2 + 1)
    num_bins = N // 2 + 1
    filterbank = [[0.0] * num_bins for _ in range(n_filters)]
    for i in range(n_filters):
        left = np.argmin(np.abs(freqs - hz_points[i]))
        center = np.argmin(np.abs(freqs - hz_points[i + 1]))
        right = np.argmin(np.abs(freqs - hz_points[i + 2]))
        for k in range(left, right + 1):
            if k == left:
                slope = (k - left) / (center - left)
            elif k == right:
                slope = (right - k) / (right - center)
            else:
                slope = 1.0
            filterbank[i][k] = slope
    if normalize:
        for i in range(n_filters):
            width_hz = hz_points[i + 2] - hz_points[i]
            scaling_factor = 2.0 / width_hz
            for k in range(num_bins):
                filterbank[i][k] *= scaling_factor
    rounded_filterbank = [[round(value, 4) for value in row] for row in filterbank]
    return rounded_filterbank

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def check_space(space: Space, is_composite: bool) -> bool:
        if is_composite:
            if isinstance(space, (Tuple, Dict)):
                for subspace in space.spaces.values() if isinstance(space, Dict) else space.spaces:
                    if not isinstance(subspace, Box):
                        return False
                return True
            else:
                return False
        else:
            return isinstance(space, Box)
    cont_action = check_space(env.action_space, tuple_action)
    cont_obs = check_space(env.observation_space, tuple_obs)
    return (cont_action, cont_obs)

