        is_tuple = any((isinstance(x, (list, tuple, np.ndarray)) for x in data))
        if is_tuple:
            sample = data[0]
            if isinstance(sample, (list, tuple)):
                dim = len(sample)
            elif isinstance(sample, np.ndarray):
                dim = sample.shape[0]
            else:
                dim = 1
        else:
            dim = 1
        multidim = dim > 1
        flat_values = []
        for item in data:
            if isinstance(item, (list, tuple, np.ndarray)):
                flat_values.extend(item)
            else:
                flat_values.append(item)
        continuous = any((not isinstance(val, int) and isinstance(val, float) for val in flat_values))
        if is_tuple:
            per_dim_unique = []
