                values_in_dim = []
                for item in data:
                    if isinstance(item, (list, tuple, np.ndarray)):
                        values_in_dim.append(item[i])
                    else:
                        values_in_dim.append(item)
                unique_vals = sorted(set(values_in_dim))
                per_dim_unique.append(unique_vals)
        else:
            unique_vals = sorted(set(flat_values))
            per_dim_unique = [unique_vals]
        return {'tuple': is_tuple, 'multidim': multidim, 'continuous': continuous, 'n_per_dim': [len(vals) for vals in per_dim_unique], 'dim': dim, 'ids': per_dim_unique}
    action_info = process_list(actions)
