    return result

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in [0, 1, 2]:
        return -1
    x_array = np.array(x) if isinstance(x, (list, np.ndarray)) else np.array([x])
    sig = 1 / (1 + np.exp(-x_array))
    if order == 0:
        result = sig
    elif order == 1:
        result = sig * (1 - sig)
    elif order == 2:
        result = sig * (1 - sig) * (1 - 2 * sig)
    result = np.round(result, 4)
    if x_array.size == 1:
        return float(result.item())
    else:
        return result.tolist()

import itertools
def apriori_frequent_itemsets(transactions: list[list], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
        Each tuple contains items in ascending (lexicographic) order.
    """
    if not transactions or min_sup <= 0 or min_sup > 1:
        return []
    freq_items = {}
    num_transactions = len(transactions)
    for transaction in transactions:
        for item in transaction:
            if item in freq_items:
                freq_items[item] += 1
            else:
                freq_items[item] = 1
    frequent_1_itemsets = {item: count for (item, count) in freq_items.items() if count / num_transactions >= min_sup}
    frequent_1_itemsets = sorted(frequent_1_itemsets.items())
    result = [tuple([item]) for (item, _) in frequent_1_itemsets]
    current_freq_itemsets = frequent_1_itemsets
    k = 2
    while current_freq_itemsets:
        candidates = generate_candidates(current_freq_itemsets, k)
        candidate_counts = {}
        for transaction in transactions:
            transaction_set = set(transaction)
            for candidate in candidates:
                if set(candidate).issubset(transaction_set):
                    if candidate in candidate_counts:
                        candidate_counts[candidate] += 1
                    else:
                        candidate_counts[candidate] = 1
        frequent_k_itemsets = [candidate for (candidate, count) in candidate_counts.items() if count / num_transactions >= min_sup]
        frequent_k_itemsets.sort()
        result.extend(frequent_k_itemsets)
        current_freq_itemsets = frequent_k_itemsets
        k += 1
    return result
def generate_candidates(prev_freq_itemsets: list[tuple], k: int) -> list[tuple]:
    """Generate candidate k-itemsets from frequent (k-1)-itemsets."""
    candidates = set()
    prev_freq_itemsets.sort()
    for i in range(len(prev_freq_itemsets)):
        for j in range(i + 1, len(prev_freq_itemsets)):
            if prev_freq_itemsets[i][:-1] == prev_freq_itemsets[j][:-1]:
                new_candidate = prev_freq_itemsets[i] + (prev_freq_itemsets[j][-1],)
                new_candidate_sorted = tuple(sorted(new_candidate))
                candidates.add(new_candidate_sorted)
    return list(candidates)

