def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    X_np = np.array(X)
    if Y is None:
        Y_np = X_np
    else:
        Y_np = np.array(Y)
    if X_np.shape[1] != Y_np.shape[1]:
        return -1
    C = X_np.shape[1]
    N = X_np.shape[0]
    M = Y_np.shape[0]
    if sigma is None:
        sigma = np.sqrt(C / 2)
    elif isinstance(sigma, float):
        if sigma <= 0:
            return -1
        sigma = [sigma] * C
    elif isinstance(sigma, list):
        if len(sigma) != C or any((s <= 0 for s in sigma)):
            return -1
    else:
        return -1
    dist_sq = np.sum(((X_np[:, np.newaxis, :] - Y_np[np.newaxis, :, :]) / sigma) ** 2, axis=2)
    K = np.exp(-0.5 * dist_sq)
    return [list(np.round(row, 4)) for row in K.tolist()]

from collections import Counter, defaultdict
import itertools
def fp_growth(transactions, min_sup):
    """
    Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : list of list of hashable
        A collection of transactions. Each transaction is a list of hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list of tuple
        All frequent item-sets sorted 1) by length, 2) lexicographically.
        Every tuple itself is sorted lexicographically.
    """
    item_counts = Counter()
    for transaction in transactions:
        item_counts.update(transaction)
    frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_sup}
    sorted_transactions = []
    for transaction in transactions:
        filtered = [item for item in transaction if item in frequent_items]
        sorted_items = sorted(filtered, key=lambda x: (-frequent_items[x], x))
        sorted_transactions.append(sorted_items)

    class FPNode:

        def __init__(self, item, count=1):
            self.item = item
            self.count = count
            self.children = {}
            self.node_link = None
            self.parent = None
    root = FPNode('root')
    header_table = {}
    for transaction in sorted_transactions:
        current_node = root
        for item in transaction:
            if item not in current_node.children:
                new_node = FPNode(item)
                current_node.children[item] = new_node
                if item not in header_table:
                    header_table[item] = new_node
                else:
                    current_header = header_table[item]
                    while current_header.node_link is not None:
                        current_header = current_header.node_link
                    current_header.node_link = new_node
            else:
                current_node.children[item].count += 1
            current_node = current_node.children[item]
    frequent_itemsets = []

    def mine_tree(header_table, prefix):
        if not header_table:
            return
        items = sorted(header_table.keys())
        for item in items:
            patterns = []
            current_node = header_table[item]
            while current_node is not None:
                path = []
                node = current_node
                while node.parent is not None:
                    path.append(node.item)
                    node = node.parent
                path.reverse()
                pattern = path[:-1]
                patterns.append((pattern, current_node.count))
                current_node = current_node.node_link
            conditional_transactions = []
            for (pattern, count) in patterns:
                for _ in range(count):
                    conditional_transactions.append(pattern)
            conditional_item_counts = Counter()
            for pattern in conditional_transactions:
                conditional_item_counts.update(pattern)
            conditional_frequent_items = {item: count for (item, count) in conditional_item_counts.items() if count >= min_sup}
            sorted_conditional_transactions = []
            for pattern in conditional_transactions:
                sorted_pattern = sorted(pattern, key=lambda x: (-conditional_frequent_items[x], x))
                sorted_conditional_transactions.append(sorted_pattern)
            conditional_header_table = {}
            conditional_root = FPNode('root')
            for trans in sorted_conditional_transactions:
                current_node = conditional_root
                for item in trans:
                    if item not in current_node.children:
                        new_node = FPNode(item)
                        current_node.children[item] = new_node
                        if item not in conditional_header_table:
                            conditional_header_table[item] = new_node
                        else:
                            current_header = conditional_header_table[item]
                            while current_header.node_link is not None:
                                current_header = current_header.node_link
                            current_header.node_link = new_node
                    else:
                        current_node.children[item].count += 1
                    current_node = current_node.children[item]
            mine_tree(conditional_header_table, prefix + (item,))
            frequent_itemsets.append((item,))
    mine_tree(header_table, ())
    unique_itemsets = set()
    for itemset in frequent_itemsets:
        sorted_itemset = tuple(sorted(itemset))
        unique_itemsets.add(sorted_itemset)
    sorted_itemsets = sorted(unique_itemsets, key=lambda x: (len(x), x))
    return sorted_itemsets

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """
    if isinstance(z, (list, tuple)):
        if not z:
            return []
        if isinstance(z[0], (list, tuple)):
            return [sigmoid_grad(item) for item in z]
        else:
            z = np.array(z)
    elif isinstance(z, np.ndarray):
        pass
    else:
        z = np.array([z])
    grad = z * (1 - z)
    grad = np.round(grad, 4)

    def convert_to_native(arr):
        if isinstance(arr, np.ndarray):
            if arr.ndim == 0:
                return float(arr.item())
            elif arr.ndim == 1:
                return [convert_to_native(item) for item in arr]
            else:
                return [convert_to_native(row) for row in arr]
        return arr
    result = convert_to_native(grad)
    return result

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if task_type == 'classification':

        def traverse_classification(node):
            if hasattr(node, 'y'):
                return node.y
            feature_value = features[node.label]
            for child in node.child:
                if child.x == feature_value:
                    return traverse_classification(child)
            return traverse_classification(node.child[1])
        return traverse_classification(root)
    elif task_type == 'regression':

        def traverse_regression(node):
            if hasattr(node, 'y'):
                return node.y
            feature_value = features[node.label]
            if feature_value <= node.s:
                return traverse_regression(node.child[0])
            else:
                return traverse_regression(node.child[1])
        return traverse_regression(root)

