    (n_samples, n_features) = data.shape
    phi = 2 * math.log(n_samples - 1) - 2 * (n_samples - 1) / n_samples if n_samples > 1 else 0

    def c(n):
        if n == 1:
            return 0
        return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    class IsolationTree:

        def __init__(self, data, height_limit):
            self.root = self._build_tree(data, depth=0, height_limit=height_limit)

        def _build_tree(self, data, depth, height_limit):
            n = data.shape[0]
            if n <= 1 or depth >= height_limit:
                return {'is_leaf': True, 'samples': data}
            feature_idx = np.random.randint(n_features)
            feature_values = data[:, feature_idx]
            (min_val, max_val) = (np.min(feature_values), np.max(feature_values))
            threshold = np.random.uniform(min_val, max_val)
            left_data = data[feature_values <= threshold]
            right_data = data[feature_values > threshold]
            left_child = self._build_tree(left_data, depth + 1, height_limit)
            right_child = self._build_tree(right_data, depth + 1, height_limit)
            return {'is_leaf': False, 'feature_idx': feature_idx, 'threshold': threshold, 'left': left_child, 'right': right_child}

        def path_length(self, x):
            node = self.root
            path_len = 0
            while not node['is_leaf']:
                feature_idx = node['feature_idx']
                threshold = node['threshold']
                if x[feature_idx] <= threshold:
                    node = node['left']
                else:
                    node = node['right']
                path_len += 1
            n_samples_in_leaf = node['samples'].shape[0]
            return path_len + c(n_samples_in_leaf)
    trees = []
    for _ in range(n_trees):
        if sample_size >= n_samples:
            subsample = data
        else:
            subsample = data[np.random.choice(n_samples, size=sample_size, replace=False)]
        height_limit = math.ceil(math.log2(subsample.shape[0])) if subsample.shape[0] > 1 else 0
        tree = IsolationTree(subsample, height_limit)
        trees.append(tree)
    path_lengths = np.zeros(n_samples)
    for i in range(n_samples):
        x = data[i]
        total_path_length = 0
        for tree in trees:
            total_path_length += tree.path_length(x)
        path_lengths[i] = total_path_length / n_trees
    scores = 2 ** (-path_lengths / phi) if phi != 0 else path_lengths
    if epsilon == 0:
        return []
    n_outliers = max(1, int(np.ceil(epsilon * n_samples)))
    outlier_indices = np.argsort(-scores)[:n_outliers]
    return sorted(outlier_indices.tolist())

import math
from collections import defaultdict
import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    """
    Trains a Maximum Entropy classifier using Generalized Iterative Scaling (GIS)
    and predicts labels for test samples.

    Parameters:
        X_train: Training features (numpy array), shape (n_samples, n_features)
        y_train: Training labels (numpy array), shape (n_samples,)
        X_test: Test features (numpy array), shape (n_samples, n_features)
        epsilon: Stopping criterion for weight updates (default: 1e-3)
        n_iter: Maximum number of iterations (default: 100)

    Returns:
        A list of predicted labels for each sample in X_test.
    """
    empirical_counts = defaultdict(int)
    label_counts = defaultdict(int)
    for (x, y) in zip(X_train, y_train):
        label_counts[y] += 1
        for (idx, val) in enumerate(x):
            if val != 0:
                key = (idx, val, y)
                empirical_counts[key] += 1
    weights = defaultdict(float)
    for ((idx, val, y), count) in empirical_counts.items():
        total = label_counts[y]
        weights[idx, val, y] = math.log(count / total)
    for _ in range(n_iter):
        normalization_factors = []
        for x in X_train:
            z = 0.0
            for (idx, val) in enumerate(x):
                if val != 0:
                    for y in np.unique(y_train):
                        key = (idx, val, y)
                        if key in weights:
                            z += math.exp(weights[key])
            normalization_factors.append(z)
        expected_counts = defaultdict(float)
        for (i, (x, y_true)) in enumerate(zip(X_train, y_train)):
            z = normalization_factors[i]
            for (idx, val) in enumerate(x):
                if val != 0:
                    for y in np.unique(y_train):
                        key = (idx, val, y)
                        if key in weights:
                            expected_counts[key] += math.exp(weights[key]) / z
        max_update = 0.0
        for ((idx, val, y), count) in empirical_counts.items():
            expected = expected_counts.get((idx, val, y), 0.0)
            if expected > 0:
                update = math.log(count / expected)
                weights[idx, val, y] += update
                max_update = max(max_update, abs(update))
        if max_update < epsilon:
            break
    predictions = []
    for x in X_test:
        max_prob = -float('inf')
        best_label = None
        for y in np.unique(y_train):
            score = 0.0
            for (idx, val) in enumerate(x):
                if val != 0:
                    key = (idx, val, y)
                    if key in weights:
                        score += weights[key]
            prob = math.exp(score)
            if prob > max_prob:
                max_prob = prob
                best_label = y
        predictions.append(best_label)
    return predictions
