def pegasos_kernel_svm(data: np.ndarray, labels: np.ndarray, kernel='linear', lambda_val=0.01, iterations=100, sigma=1.0):
    (n_samples, n_features) = data.shape

    def linear_kernel(x, y):
        return np.dot(x, y.T)

    def rbf_kernel(x, y):
        squared_dist = np.sum((x[:, None] - y[None]) ** 2, axis=-1)
        return np.exp(-squared_dist / (2 * sigma ** 2))
    if kernel == 'linear':
        K = linear_kernel(data, data)
    elif kernel == 'rbf':
        K = rbf_kernel(data, data)
    else:
        raise ValueError("Unsupported kernel type. Use 'linear' or 'rbf'.")
    alpha = np.zeros(n_samples)
    b = 0.0
    for t in range(1, iterations + 1):
        learning_rate = 1.0 / (lambda_val * t)
        for i in range(n_samples):
            yi = labels[i]
            Ki = K[i, :]
            pred = yi * (np.dot(alpha * labels, Ki) + b)
            if pred <= 1:
                alpha[i] += learning_rate * yi
                b += learning_rate * yi
    return (np.round(alpha, 4).tolist(), round(float(b), 4))
assert pegasos_kernel_svm(np.array([[1, 2], [2, 3], [3, 1], [4, 1]]), np.array([1, 1, -1, -1]), kernel='linear', lambda_val=0.01, iterations=100) == ([100.0, 0.0, -100.0, -100.0], -937.4755)
