class Dense(Layer):

    def __init__(self, n_units, input_shape=None):
        super().__init__()
        self.layer_input = None
        self.input_shape = input_shape
        self.n_units = n_units
        self.trainable = True
        self.W = None
        self.w0 = None
        self.optimizer = None

    def initialize(self, optimizer):
        fan_in = self.input_shape[0]
        limit = 1 / math.sqrt(fan_in)
        self.W = np.random.uniform(-limit, limit, (fan_in, self.n_units))
        self.w0 = np.zeros((1, self.n_units))
        self.optimizer = copy.copy(optimizer)

    def parameters(self):
        return self.W.size + self.w0.size

    def forward_pass(self, X, training=True):
        self.layer_input = X
        output = np.dot(X, self.W) + self.w0
        return np.round(output, 4)

    def backward_pass(self, accum_grad):
        accum_grad = accum_grad.reshape((-1, self.n_units))
        grad_W = np.dot(self.layer_input.T, accum_grad)
        grad_w0 = np.sum(accum_grad, axis=0, keepdims=True)
        if self.trainable:
            (self.W, self.w0) = self.optimizer.update(grads=[grad_W, grad_w0], params=[self.W, self.w0])
        accum_grad_next = np.dot(accum_grad, self.W.T)
        return np.round(accum_grad_next, 4).tolist()

    def output_shape(self):
        return (self.n_units,)
