def calculate_brightness(img):
    if not img:
        return -1
    try:
        row_lengths = [len(row) for row in img]
        if len(set(row_lengths)) != 1:
            return -1
        total_pixels = 0
        sum_brightness = 0
        for row in img:
            for pixel in row:
                if not isinstance(pixel, int) or not 0 <= pixel <= 255:
                    return -1
                sum_brightness += pixel
                total_pixels += 1
        average_brightness = round(sum_brightness / total_pixels, 2)
        return average_brightness
    except Exception as e:
        return -1
