from scipy import stats
def descriptive_statistics(data):
    if len(data) == 0:
        raise ValueError('Data list is empty')
    data_array = np.array(data, dtype=float)
    mean = round(np.mean(data_array), 4)
    median = round(np.median(data_array), 4)
    mode = round(stats.mode(data_array).mode[0], 4)
    variance = round(np.var(data_array, ddof=1), 4)
    std_dev = round(np.std(data_array, ddof=1), 4)
    percentiles = np.percentile(data_array, [25, 50, 75])
    (p25, p50, p75) = [round(p, 4) for p in percentiles]
    iqr = round(p75 - p25, 4)
    return {'mean': mean, 'median': median, 'mode': mode, 'variance': variance, 'standard_deviation': std_dev, '25th_percentile': p25, '50th_percentile': p50, '75th_percentile': p75, 'interquartile_range': iqr}
assert descriptive_statistics([10, 20, 30, 40, 50]) == {'mean': 30.0, 'median': 30.0, 'mode': 10, 'variance': 200.0, 'standard_deviation': 14.1421, '25th_percentile': 20.0, '50th_percentile': 30.0, '75th_percentile': 40.0, 'interquartile_range': 20.0}
