from typing import Dict, List, Optional, Any
def create_encoding_dicts(obs_ids: list, action_ids: list, obs_encoder: dict | None=None, act_encoder: dict | None=None) -> dict:
    """Generate forward and backward mappings for observations and actions.

    Args:
        obs_ids (list): All distinct observation identifiers.
        action_ids (list): All distinct action identifiers.
        obs_encoder (dict | None, optional): Existing obs→number mapping.
        act_encoder (dict | None, optional): Existing act→number mapping.

    Returns:
        dict: A dictionary that contains the keys 'obs2num', 'num2obs',
        'act2num', and 'num2act'.
    """
    result = {}
    if obs_encoder is not None:
        obs2num = obs_encoder
        num2obs = {v: k for (k, v) in obs_encoder.items()}
    elif len(obs_ids) == 0:
        obs2num = {}
        num2obs = {}
    else:
        obs2num = {x: i for (i, x) in enumerate(obs_ids)}
        num2obs = {i: x for (i, x) in enumerate(obs_ids)}
    result['obs2num'] = obs2num
    result['num2obs'] = num2obs
    if act_encoder is not None:
        act2num = act_encoder
        num2act = {v: k for (k, v) in act_encoder.items()}
    elif len(action_ids) == 0:
        act2num = {}
        num2act = {}
    else:
        act2num = {a: i for (i, a) in enumerate(action_ids)}
        num2act = {i: a for (i, a) in enumerate(action_ids)}
    result['act2num'] = act2num
    result['num2act'] = num2act
    return result

import numpy as np
def sigmoid(x):
    """Implement element-wise logistic sigmoid."""

    def _sigmoid(z):
        return 1 / (1 + np.exp(-z))
    result = _sigmoid(np.array(x, dtype=float))
    if np.isscalar(x):
        return round(float(result), 4)
    return np.round(result, 4)
def tanh(x):
    """Implement element-wise hyperbolic tangent."""
    result = np.tanh(np.array(x, dtype=float))
    if np.isscalar(x):
        return round(float(result), 4)
    return np.round(result, 4)
def relu(x):
    """Implement element-wise Rectified Linear Unit."""
    x_arr = np.array(x, dtype=float)
    result = np.maximum(0, x_arr)
    if np.isscalar(x):
        return round(float(result), 4)
    return np.round(result, 4)
def leaky_relu(x, alpha: float=0.01):
    """Implement element-wise Leaky-ReLU with slope *alpha* for x<0."""
    x_arr = np.array(x, dtype=float)
    result = np.where(x_arr > 0, x_arr, x_arr * alpha)
    if np.isscalar(x):
        return round(float(result), 4)
    return np.round(result, 4)
def softmax(x):
    """Implement numerically stable softmax over a 1-D input."""
    x_arr = np.array(x, dtype=float)
    exps = np.exp(x_arr - np.max(x_arr))
    result = exps / np.sum(exps)
    if np.isscalar(x):
        return round(float(result), 4)
    return np.round(result, 4)
def get_activation(name):
    """Return the activation function by *name*.

    Args:
        name (str): One of {"sigmoid", "tanh", "relu", "leaky_relu", "softmax"}

    Returns:
        Callable: the requested activation function.

    Raises:
        ValueError: If *name* is not recognised.
    """
    activations = {'sigmoid': sigmoid, 'tanh': tanh, 'relu': relu, 'leaky_relu': leaky_relu, 'softmax': softmax}
    if name not in activations:
        raise ValueError('Invalid activation function.')
    return activations[name]

import numpy as np
def feedforward_predict(x, weights, activations):
    """Runs a forward pass through a fully-connected neural network."""
    a = np.array(x, dtype=float)
    for layer in range(len(weights)):
        a_with_bias = np.insert(a, 0, 1)
        W = np.array(weights[layer], dtype=float)
        z = W @ a_with_bias
        if activations[layer] == 'sigmoid':
            a = 1 / (1 + np.exp(-z))
        elif activations[layer] == 'relu':
            a = np.maximum(0, z)
        elif activations[layer] == 'linear':
            a = z.copy()
        else:
            raise ValueError(f'Unknown activation function: {activations[layer]}')
    output = np.round(a, 4)
    return float(output[0]) if output.size == 1 else output.tolist()

import numpy as np
from itertools import combinations
def random_unweighted_graph(n_vertices: int, edge_prob: float=0.5, directed: bool=False) -> list[list[int]]:
    """Generate an un-weighted Erdős–Rényi random graph.

    The function must return the adjacency matrix (a nested list) of the graph.

    Args:
        n_vertices (int): Number of vertices in the graph (labelled 0..n-1).
        edge_prob  (float): Probability *p* that an admissible edge exists.
        directed   (bool): Whether the graph is directed (default ``False``).

    Returns:
        list[list[int]]: The adjacency matrix *A* where *A[i][j] == 1* iff an
        edge from vertex *i* to vertex *j* exists.  The matrix must consist of
        integers (0/1) only and have shape *(n_vertices, n_vertices)*.
    """
    if n_vertices == 0:
        return []
    if n_vertices == 1:
        return [[0]]
    size = n_vertices
    adj_matrix = [[0] * size for _ in range(size)]
    if directed:
        for i in range(size):
            for j in range(size):
                if i != j:
                    if np.random.rand() <= edge_prob:
                        adj_matrix[i][j] = 1
    else:
        for (i, j) in combinations(range(size), 2):
            if np.random.rand() <= edge_prob:
                adj_matrix[i][j] = 1
                adj_matrix[j][i] = 1
    return adj_matrix

def evaluate_decision_tree(tree: dict, sample: list[float]):
    """Traverse a binary decision-tree represented by nested dictionaries.

    Args:
        tree (dict): Root node of the decision tree. Internal nodes contain
            'feature_i', 'threshold', 'true_branch', 'false_branch'. Leaf
            nodes contain only 'value'.
        sample (list[float]): Feature vector that will be classified/regressed.

    Returns:
        The value stored in the reached leaf (int, float, or str).
    """
    if 'value' in tree:
        return tree['value']
    feature_i = tree['feature_i']
    threshold = tree['threshold']
    if sample[feature_i] <= threshold:
        return evaluate_decision_tree(tree['true_branch'], sample)
    else:
        return evaluate_decision_tree(tree['false_branch'], sample)

import numpy as np
class L1Regularization:

    def __init__(self, alpha: float):
        """Initialize L1 regularization with strength alpha."""
        if alpha < 0:
            raise ValueError('Alpha must be a non-negative float.')
        self.alpha = alpha

    def __call__(self, w: np.ndarray) -> float:
        """Compute the L1 penalty value: J(w) = alpha * sum(|w_i|)."""
        if self.alpha == 0:
            return 0.0
        return self.alpha * np.sum(np.abs(w))

    def grad(self, w: np.ndarray) -> np.ndarray:
        """Compute the gradient of the L1 penalty: grad(J) = alpha * sign(w)."""
        if self.alpha == 0:
            return np.zeros_like(w)
        return self.alpha * np.sign(w)

import numpy as np
def squared_error(actual, predicted):
    """Calculate the squared error or mean squared error.

    If *actual* and *predicted* are scalars, return the squared difference.
    If they are one-dimensional iterables (same length), return the mean squared
    error. Return -1 for length mismatches or empty iterables.

    All results must be rounded to 4 decimal places.
    """
    try:
        len(actual)
        is_iterable = True
    except TypeError:
        is_iterable = False
    if not is_iterable:
        squared_diff = (actual - predicted) ** 2
        return round(squared_diff, 4)
    actual = np.array(actual)
    predicted = np.array(predicted)
    if len(actual.shape) != 1 or len(predicted.shape) != 1:
        return -1
    if len(actual) != len(predicted) or len(actual) == 0:
        return -1
    mse = np.mean((actual - predicted) ** 2)
    return round(mse, 4)

import numpy as np
from dataclasses import dataclass
@dataclass
class Leaf:
    """A terminal node that stores a prediction value."""
    value: object
@dataclass
class Node:
    """An internal decision-tree node.

    Attributes:
        feature (int):   Index of the feature to test.
        threshold (float): Threshold that splits the data.
        left (Node | Leaf):  Sub-tree for samples with feature value < threshold.
        right (Node | Leaf): Sub-tree for samples with feature value ≥ threshold.
    """
    feature: int
    threshold: float
    left: object
    right: object
def compare_trees(tree_a, tree_b):
    """Recursively checks whether *tree_a* and *tree_b* are equivalent.

    Args:
        tree_a: Root node of the first decision tree (Node or Leaf).
        tree_b: Root node of the second decision tree (Node or Leaf).

    Returns:
        True if the two trees are equivalent, False otherwise.
    """
    if isinstance(tree_a, Leaf) and isinstance(tree_b, Leaf):
        try:
            return np.allclose(tree_a.value, tree_b.value, atol=1e-08)
        except:
            return tree_a.value == tree_b.value
    if isinstance(tree_a, Node) and isinstance(tree_b, Node):
        if tree_a.feature != tree_b.feature:
            return False
        if not np.isclose(tree_a.threshold, tree_b.threshold, atol=1e-08):
            return False
        return compare_trees(tree_a.left, tree_b.left) and compare_trees(tree_a.right, tree_b.right)
    return False

import heapq
from typing import List, Union
Number = Union[int, float]
def top_k_elements(sequence: List[Number], k: int, order: str='largest') -> List[Number]:
    """Keeps only the k largest or k smallest numbers from *sequence*.

    The function must visit each element exactly once and store no more than
    *k* items at any time by making use of the heapq module (binary heap).

    Args:
        sequence: A list of integers or floats representing the data stream.
        k: The number of extreme values to keep.
        order: "largest" to retain the k biggest values, "smallest" to retain
            the k smallest.

    Returns:
        A list with at most k numbers sorted according to *order*.
    """
    if k <= 0 or not sequence:
        return []
    if k >= len(sequence):
        if order == 'largest':
            return sorted(sequence, reverse=True)
        elif order == 'smallest':
            return sorted(sequence)
        else:
            raise ValueError("Invalid value for 'order'. Use 'largest' or 'smallest'.")
    if order == 'largest':
        heap = []
        for num in sequence:
            if len(heap) < k:
                heapq.heappush(heap, num)
            elif num > heap[0]:
                heapq.heappushpop(heap, num)
        return sorted(heap, reverse=True)
    elif order == 'smallest':
        heap = []
        for num in sequence:
            if len(heap) < k:
                heapq.heappush(heap, -num)
            elif num < -heap[0]:
                heapq.heappushpop(heap, -num)
        return sorted((-x for x in heap))
    else:
        raise ValueError("Invalid value for 'order'. Use 'largest' or 'smallest'.")

class Node:

    def __init__(self, key, val):
        self.key = key
        self.val = val
        self.left = None
        self.right = None

    def __lt__(self, other):
        if isinstance(other, Node):
            return self.val < other.val
        return NotImplemented

    def __le__(self, other):
        if isinstance(other, Node):
            return self.val <= other.val
        return NotImplemented

    def __gt__(self, other):
        if isinstance(other, Node):
            return self.val > other.val
        return NotImplemented

    def __ge__(self, other):
        if isinstance(other, Node):
            return self.val >= other.val
        return NotImplemented

    def __eq__(self, other):
        if isinstance(other, Node):
            return self.val == other.val
        return NotImplemented
def inorder_keys(pairs):
    """Build a BST from (key, numeric value) *pairs* and return keys from an in-order traversal.

    Args:
        pairs (list[tuple[Any, int | float]]): List of (key, numeric value) pairs.

    Returns:
        list: Keys in in-order (ascending by numeric value). Return an empty
        list if *pairs* is empty.
    """
    if not pairs:
        return []

    class Node:

        def __init__(self, key, val):
            self.key = key
            self.val = val
            self.left = None
            self.right = None

        def __lt__(self, other):
            if isinstance(other, Node):
                return self.val < other.val
            return NotImplemented

        def __le__(self, other):
            if isinstance(other, Node):
                return self.val <= other.val
            return NotImplemented

        def __gt__(self, other):
            if isinstance(other, Node):
                return self.val > other.val
            return NotImplemented

        def __ge__(self, other):
            if isinstance(other, Node):
                return self.val >= other.val
            return NotImplemented

        def __eq__(self, other):
            if isinstance(other, Node):
                return self.val == other.val
            return NotImplemented
    root = Node(pairs[0][0], pairs[0][1])
    for (key, val) in pairs[1:]:
        current = root
        new_node = Node(key, val)
        while True:
            if new_node < current:
                if current.left is None:
                    current.left = new_node
                    break
                else:
                    current = current.left
            elif current.right is None:
                current.right = new_node
                break
            else:
                current = current.right
    result = []

    def inorder(node):
        if node:
            inorder(node.left)
            result.append(node.key)
            inorder(node.right)
    inorder(root)
    return result

import numpy as np
def bayesian_posterior_mean(X: np.ndarray, y: np.ndarray, alpha: float=1.0, beta: float=1.0, mu=0.0, V=None, fit_intercept: bool=True) -> list[float]:
    """
    Compute the posterior mean (MAP estimate) of the coefficients in Bayesian linear regression.
    """
    X = X.copy()
    if fit_intercept:
        X = np.hstack([np.ones((X.shape[0], 1)), X])
    (N, M) = X.shape
    if np.isscalar(mu):
        mu = np.full(M, mu)
    else:
        mu = np.array(mu)
    if V is None:
        V_inv = np.eye(M)
    elif np.isscalar(V):
        V_inv = 1 / V * np.eye(M)
    elif isinstance(V, (list, tuple, np.ndarray)):
        V = np.array(V)
        if V.ndim == 1:
            V_inv = np.diag(1 / V)
        else:
            V_inv = np.linalg.inv(V)
    else:
        raise ValueError('Invalid type for V')
    XtX = X.T @ X
    V_inv_plus_XtX = V_inv + XtX
    Sigma_b = np.linalg.inv(V_inv_plus_XtX)
    Xt_y = X.T @ y
    V_inv_mu = V_inv @ mu
    mu_b = Sigma_b @ (V_inv_mu + Xt_y)
    return [round(float(x), 4) for x in mu_b]

import numpy as np
def svm_predict(X: list[list[float]], support_vectors: list[list[float]], lagr_multipliers: list[float], support_vector_labels: list[int], intercept: float, kernel: str='rbf', power: int=3, gamma: float | None=None, coef: float=1.0) -> list[int]:
    """Predict labels for a batch samples using a pre-trained SVM."""
    X = np.array(X)
    support_vectors = np.array(support_vectors)
    n_features = X.shape[1]
    if gamma is None:
        gamma = 1.0 / n_features
    decision_values = np.zeros(X.shape[0])
    for (i, (sv, alpha, label)) in enumerate(zip(support_vectors, lagr_multipliers, support_vector_labels)):
        sv = sv.reshape(1, -1)
        if kernel == 'linear':
            kernel_values = np.sum(sv * X, axis=1)
        elif kernel == 'polynomial':
            kernel_values = (gamma * np.sum(sv * X, axis=1) + coef) ** power
        elif kernel == 'rbf':
            diff = X - sv
            kernel_values = np.exp(-gamma * np.sum(diff * diff, axis=1))
        else:
            raise ValueError(f'Unknown kernel: {kernel}')
        decision_values += alpha * label * kernel_values
    decision_values += intercept
    predictions = np.where(decision_values >= 0, 1, -1)
    return predictions.tolist()

def update_kernel_settings(parameters: dict, hyperparameters: dict, summary_dict: dict) -> tuple[dict, dict]:
    """Update kernel parameters and hyperparameters.

    The function must not modify the input dictionaries.  It should return two
    *new* dictionaries in which the supplied `summary_dict` updates have been
    applied following the rules explained in the task description.

    Args:
        parameters (dict):      Existing kernel parameters.
        hyperparameters (dict): Existing kernel hyperparameters.
        summary_dict (dict):    Dictionary that may contain the keys
            "parameters" and/or "hyperparameters" whose values are dictionaries
            of updates.  Any top-level keys are also treated as updates.

    Returns:
        tuple[dict, dict]: `(new_parameters, new_hyperparameters)` – the
        updated dictionaries.
    """
    new_parameters = parameters.copy()
    new_hyperparameters = hyperparameters.copy()
    updates = {}
    if 'parameters' in summary_dict:
        updates.update(summary_dict['parameters'])
    if 'hyperparameters' in summary_dict:
        updates.update(summary_dict['hyperparameters'])
    for (key, value) in summary_dict.items():
        if key not in ('parameters', 'hyperparameters'):
            updates[key] = value
    for (key, value) in updates.items():
        if key in new_parameters:
            new_parameters[key] = value
        elif key in new_hyperparameters:
            new_hyperparameters[key] = value
    return (new_parameters, new_hyperparameters)

from collections import Counter
def build_fp_tree(transactions: list[list[str]], min_support: int) -> dict:
    """Construct an FP-Tree using only nested dictionaries."""
    item_support = Counter()
    for transaction in transactions:
        item_support.update(set(transaction))
    frequent_items = {item for (item, count) in item_support.items() if count >= min_support}
    ordered_items = sorted(frequent_items, key=lambda x: (-item_support[x], x))
    item_order_index = {item: idx for (idx, item) in enumerate(ordered_items)}
    fp_tree = {'support': 0, 'children': {}}
    for transaction in transactions:
        filtered = [item for item in transaction if item in frequent_items]
        ordered_transaction = sorted(set(filtered), key=lambda x: item_order_index[x])
        current_level = fp_tree['children']
        for item in ordered_transaction:
            if item not in current_level:
                current_level[item] = {'support': 0, 'children': {}}
            current_level[item]['support'] += 1
            current_level = current_level[item]['children']
    return fp_tree

import numpy as np
def get_initializer(name: str):
    """Return a weight-initialisation callable associated with *name*.

    The callable must have the signature
        initializer(shape: tuple[int, ...], scale: float = 1.0) -> np.ndarray
    and create a numpy array filled according to the requested strategy.

    Supported strategies (case-insensitive):
        - "zeros"
        - "ones"
        - "normal"
        - "glorot_uniform"

    If *name* is not recognised the function must raise `ValueError`.
    """
    name = name.lower()
    if name == 'zeros':

        def zeros_initializer(shape: tuple, scale: float=1.0) -> np.ndarray:
            return np.zeros(shape)
        return zeros_initializer
    elif name == 'ones':

        def ones_initializer(shape: tuple, scale: float=1.0) -> np.ndarray:
            return np.ones(shape) * scale
        return ones_initializer
    elif name == 'normal':

        def normal_initializer(shape: tuple, scale: float=1.0) -> np.ndarray:
            return np.random.normal(0, scale, shape)
        return normal_initializer
    elif name == 'glorot_uniform':

        def glorot_uniform_initializer(shape: tuple, scale: float=1.0) -> np.ndarray:
            if len(shape) < 2:
                raise ValueError('Glorot uniform initializer requires at least two dimensions.')
            (fan_in, fan_out) = (shape[0], shape[1])
            limit = scale * np.sqrt(6 / (fan_in + fan_out))
            return np.random.uniform(-limit, limit, shape)
        return glorot_uniform_initializer
    else:
        raise ValueError(f'Unknown initializer name: {name}')

import heapq
from collections import Counter
class HuffmanNode:

    def __init__(self, token: str | None, freq: int):
        self.token = token
        self.freq = freq
        self.left = None
        self.right = None

    def __lt__(self, other):
        if self.freq != other.freq:
            return self.freq < other.freq
        return str(self.token or '') < str(other.token or '')
def huffman_encode(tokens: list[str]) -> dict[str, str]:
    """Generate a Huffman code for the supplied tokens."""
    if not tokens:
        return {}
    freq = Counter(tokens)
    if len(freq) == 1:
        return {next(iter(freq)): '0'}
    heap = [HuffmanNode(token, count) for (token, count) in freq.items()]
    heapq.heapify(heap)
    while len(heap) > 1:
        left = heapq.heappop(heap)
        right = heapq.heappop(heap)
        merged = HuffmanNode(None, left.freq + right.freq)
        merged.left = left
        merged.right = right
        heapq.heappush(heap, merged)
    codes = {}

    def traverse(node: HuffmanNode, prefix: str):
        if node.token is not None:
            codes[node.token] = prefix
            return
        if node.left:
            traverse(node.left, prefix + '0')
        if node.right:
            traverse(node.right, prefix + '1')
    root = heap[0]
    traverse(root, '')
    return codes

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def check_space(space: Space, is_composite: bool) -> bool:
        """Helper function to check if a space is continuous."""
        if is_composite:
            if isinstance(space, (Tuple, Dict)):
                return all((isinstance(subspace, Box) for subspace in space.spaces.values()))
            else:
                return False
        else:
            return isinstance(space, Box)
    cont_action = check_space(env.action_space, tuple_action)
    cont_obs = check_space(env.observation_space, tuple_obs)
    return (cont_action, cont_obs)

def initialize_kernel(param: Optional[Any]=None) -> KernelBase:
    """Create and return a kernel object from various representations.

    Args:
        param: None (default LinearKernel), an existing KernelBase instance, a
            string description such as 'RBF(gamma=0.5)', or a dictionary with a
            'hyperparameters' entry as produced by KernelBase.summary().

    Returns:
        KernelBase: the newly created or forwarded kernel instance.
    """
    if param is None:
        return LinearKernel()
    if isinstance(param, KernelBase):
        return param
    if isinstance(param, dict):
        if 'hyperparameters' not in param:
            raise ValueError("Dictionary must contain 'hyperparameters' key")
        hp = param['hyperparameters']
        if not isinstance(hp, dict):
            raise ValueError("'hyperparameters' must be a dictionary")
        kernel_id = hp.get('id')
        kwargs = {k: v for (k, v) in hp.items() if k != 'id'}
        if kernel_id == 'LinearKernel':
            return LinearKernel(**kwargs)
        elif kernel_id == 'PolynomialKernel':
            return PolynomialKernel(**kwargs)
        elif kernel_id == 'RBFKernel':
            return RBFKernel(**kwargs)
        else:
            raise NotImplementedError(f'Unknown kernel: {kernel_id}')
    if isinstance(param, str):
        match = re.match('(\\w+)(?:$([^)]*)$)?', param.strip())
        if not match:
            raise ValueError(f'Invalid kernel string format: {param}')
        kernel_name = match.group(1).lower()
        kwargs = {}
        if match.group(2):
            args_str = match.group(2)
            for arg in args_str.split(','):
                if '=' in arg:
                    (key, value) = arg.strip().split('=', 1)
                    key = key.strip()
                    value = value.strip()
                    try:
                        value = int(value)
                    except ValueError:
                        try:
                            value = float(value)
                        except ValueError:
                            if value.lower() == 'true':
                                value = True
                            elif value.lower() == 'false':
                                value = False
                            elif value.lower() == 'none':
                                value = None
                            else:
                                value = value.strip('"').strip("'")
                    kwargs[key] = value
        if kernel_name == 'linear':
            return LinearKernel()
        elif kernel_name == 'polynomial':
            return PolynomialKernel(**kwargs)
        elif kernel_name == 'rbf':
            return RBFKernel(**kwargs)
        else:
            raise NotImplementedError(f'Unknown kernel: {kernel_name}')
    raise NotImplementedError(f'Unsupported parameter type: {type(param)}')

import numpy as np
def gradient_boosting_predict(updates: list[np.ndarray], learning_rate: float, regression: bool) -> np.ndarray:
    """Aggregate the outputs of Gradient Boosting trees."""
    running_pred = np.zeros_like(updates[0])
    for u in updates:
        running_pred -= learning_rate * u
    if regression:
        return np.round(running_pred, 4)
    else:
        logits = running_pred
        logits -= np.max(logits, axis=1, keepdims=True)
        exp_logits = np.exp(logits)
        probs = exp_logits / np.sum(exp_logits, axis=1, keepdims=True)
        return np.argmax(probs, axis=1)

import re
def parse_activation(act_str: str):
    """Parse an activation-function description string.

    The string can be one of the following forms:
        • "ReLU"
        • "Sigmoid"
        • "Tanh"
        • "Affine(slope=<number>, intercept=<number>)"
        • "Leaky ReLU(alpha=<number>)"

    Args:
        act_str: Description of the activation function.

    Returns:
        A dictionary with key "type" and, when applicable, its parameters.
        If the input format is invalid, return -1.
    """
    if act_str == 'ReLU':
        return {'type': 'ReLU'}
    elif act_str == 'Sigmoid':
        return {'type': 'Sigmoid'}
    elif act_str == 'Tanh':
        return {'type': 'Tanh'}
    leaky_relu_match = re.fullmatch('Leaky ReLU$alpha=([-+]?(?:\\d+(?:\\.\\d*)?|\\.\\d+)(?:[eE][-+]?\\d+)?)$', act_str)
    if leaky_relu_match:
        return {'type': 'Leaky ReLU', 'alpha': float(leaky_relu_match.group(1))}
    affine_match = re.fullmatch('Affine$slope=([-+]?(?:\\d+(?:\\.\\d*)?|\\.\\d+)(?:[eE][-+]?\\d+)?), intercept=([-+]?(?:\\d+(?:\\.\\d*)?|\\.\\d+)(?:[eE][-+]?\\d+)?)$', act_str)
    if affine_match:
        return {'type': 'Affine', 'slope': float(affine_match.group(1)), 'intercept': float(affine_match.group(2))}
    return -1

import numpy as np
def env_stats(actions: list, observations: list) -> dict:
    """Compute statistics for collections of actions and observations."""

    def analyze_data(data):
        is_tuple = any((isinstance(x, (list, tuple, np.ndarray)) for x in data))
        first_elem = data[0]
        if isinstance(first_elem, (list, tuple, np.ndarray)):
            is_multidim = len(first_elem) > 1
            dim = len(first_elem)
        else:
            is_multidim = False
            dim = 1
        flat_data = []
        for x in data:
            if isinstance(x, (list, tuple, np.ndarray)):
                flat_data.extend(x)
            else:
                flat_data.append(x)
        is_continuous = any((isinstance(x, float) and (not x.is_integer()) for x in flat_data))
        dim_values = {}
        for x in data:
            if isinstance(x, (list, tuple, np.ndarray)):
                for (i, val) in enumerate(x):
                    if i not in dim_values:
                        dim_values[i] = set()
                    dim_values[i].add(val)
            else:
                if 0 not in dim_values:
                    dim_values[0] = set()
                dim_values[0].add(x)
        sorted_dims = sorted(dim_values.keys())
        n_per_dim = [len(dim_values[i]) for i in sorted_dims]
        ids_per_dim = [[val for val in sorted(dim_values[i])] for i in sorted_dims]
        return {'is_tuple': is_tuple, 'is_multidim': is_multidim, 'is_continuous': is_continuous, 'n_per_dim': n_per_dim, 'dim': dim, 'ids': ids_per_dim}
    actions_analysis = analyze_data(actions)
    obs_analysis = analyze_data(observations)
    result = {'tuple_actions': actions_analysis['is_tuple'], 'tuple_observations': obs_analysis['is_tuple'], 'multidim_actions': actions_analysis['is_multidim'], 'multidim_observations': obs_analysis['is_multidim'], 'continuous_actions': actions_analysis['is_continuous'], 'continuous_observations': obs_analysis['is_continuous'], 'n_actions_per_dim': actions_analysis['n_per_dim'], 'action_dim': actions_analysis['dim'], 'n_obs_per_dim': obs_analysis['n_per_dim'], 'obs_dim': obs_analysis['dim'], 'action_ids': actions_analysis['ids'], 'obs_ids': obs_analysis['ids']}
    return result

def get_metric(name: str):
    """Return an evaluation metric function by *name*.

    Supported names:
    - 'accuracy'
    - 'precision'
    - 'recall'
    - 'f1'

    If the name is invalid, the function should return -1.
    The returned metric functions must accept two equal-length iterables
    (y_true, y_pred) containing only 0s and 1s and return the metric value
    rounded to 4 decimal places.
    """

    def accuracy(y_true, y_pred):
        tp = fp = tn = fn = 0
        for (true, pred) in zip(y_true, y_pred):
            if true == 1 and pred == 1:
                tp += 1
            elif true == 0 and pred == 1:
                fp += 1
            elif true == 0 and pred == 0:
                tn += 1
            else:
                fn += 1
        total = tp + fp + tn + fn
        correct = tp + tn
        return round(correct / total if total else 0, 4)

    def precision(y_true, y_pred):
        tp = fp = 0
        for (true, pred) in zip(y_true, y_pred):
            if true == 1 and pred == 1:
                tp += 1
            elif true == 0 and pred == 1:
                fp += 1
        denom = tp + fp
        return round(tp / denom if denom else 0, 4)

    def recall(y_true, y_pred):
        tp = fn = 0
        for (true, pred) in zip(y_true, y_pred):
            if true == 1 and pred == 1:
                tp += 1
            elif true == 1 and pred == 0:
                fn += 1
        denom = tp + fn
        return round(tp / denom if denom else 0, 4)

    def f1(y_true, y_pred):
        p = precision(y_true, y_pred)
        r = recall(y_true, y_pred)
        denom = p + r
        return round(2 * p * r / denom if denom else 0, 4)
    metrics = {'accuracy': accuracy, 'precision': precision, 'recall': recall, 'f1': f1}
    return metrics.get(name, lambda *args: -1)

import numpy as np
def gbdt_regression(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_estimators: int=10, learning_rate: float=0.1, max_depth: int=3) -> list[float]:
    """Gradient Boosting Decision Tree (GBDT) regressor.

    Parameters and return values as described in the problem.
    """

    def mse(y_true, y_pred):
        return np.mean((y_true - y_pred) ** 2)

    class Node:

        def __init__(self, feature=None, threshold=None, left=None, right=None, value=None):
            self.feature = feature
            self.threshold = threshold
            self.left = left
            self.right = right
            self.value = value

    class DecisionTreeRegressor:

        def __init__(self, max_depth):
            self.max_depth = max_depth
            self.root = None

        def fit(self, X, y):
            self.root = self._grow_tree(X, y)

        def _grow_tree(self, X, y, depth=0):
            (n_samples, n_features) = X.shape
            if depth >= self.max_depth or len(np.unique(y)) == 1:
                return Node(value=np.mean(y))
            best_mse = mse(y, np.mean(y))
            best_split = None
            for feat_idx in range(n_features):
                thresholds = np.unique(X[:, feat_idx])
                for t in thresholds:
                    left_mask = X[:, feat_idx] <= t
                    right_mask = X[:, feat_idx] > t
                    if np.sum(left_mask) == 0 or np.sum(right_mask) == 0:
                        continue
                    y_left = y[left_mask]
                    y_right = y[right_mask]
                    current_mse = (len(y_left) * mse(y_left, np.mean(y_left)) + len(y_right) * mse(y_right, np.mean(y_right))) / n_samples
                    if current_mse < best_mse:
                        best_mse = current_mse
                        best_split = (feat_idx, t)
            if best_split is None:
                return Node(value=np.mean(y))
            (feat_idx, threshold) = best_split
            left_mask = X[:, feat_idx] <= threshold
            right_mask = X[:, feat_idx] > threshold
            left = self._grow_tree(X[left_mask], y[left_mask], depth + 1)
            right = self._grow_tree(X[right_mask], y[right_mask], depth + 1)
            return Node(feature=feat_idx, threshold=threshold, left=left, right=right)

        def predict(self, X):
            return np.array([self._predict_sample(x, self.root) for x in X])

        def _predict_sample(self, x, node):
            if node.value is not None:
                return node.value
            if x[node.feature] <= node.threshold:
                return self._predict_sample(x, node.left)
            else:
                return self._predict_sample(x, node.right)
    predictions = np.full_like(y_train, fill_value=np.mean(y_train), dtype=float)
    trees = []
    for _ in range(n_estimators):
        residuals = y_train - predictions
        tree = DecisionTreeRegressor(max_depth=max_depth)
        tree.fit(X_train, residuals)
        trees.append(tree)
        predictions += learning_rate * tree.predict(X_train)
    final_predictions = np.full(shape=len(X_test), fill_value=np.mean(y_train), dtype=float)
    for tree in trees:
        final_predictions += learning_rate * tree.predict(X_test)
    return [round(float(p), 4) for p in final_predictions]

from typing import List, Dict, Set, Tuple
def topological_sort(vertices: list, edges: list[tuple]) -> list | None:
    """Determine a topological ordering of a directed graph.

    The implementation must use depth-first search (DFS) and follow the
    deterministic exploration rule described in the task description.

    Args:
        vertices: List of vertex identifiers (hashable).
        edges:    List of directed edges given as 2-tuples (source, target).

    Returns:
        • List of vertices in topological order when the graph is acyclic.
        • ``None`` when the graph contains at least one directed cycle.
    """
    graph: Dict = {v: [] for v in vertices}
    for (u, v) in edges:
        graph[u].append(v)
    for v in vertices:
        graph[v].sort(key=lambda x: vertices.index(x))
    visited: Set = set()
    visiting: Set = set()
    result: List = []

    def dfs(node):
        if node in visiting:
            return False
        if node in visited:
            return True
        visited.add(node)
        visiting.add(node)
        for neighbor in graph[node]:
            if not dfs(neighbor):
                return False
        visiting.remove(node)
        result.append(node)
        return True
    for vertex in vertices:
        if vertex not in visited:
            if not dfs(vertex):
                return None
    return result[::-1]

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in {0, 1, 2}:
        return -1
    input_type = type(x)
    x = np.array(x, dtype=float)
    sig = 1 / (1 + np.exp(-x))
    if order == 0:
        result = sig
    elif order == 1:
        result = sig * (1 - sig)
    elif order == 2:
        result = sig * (1 - sig) * (1 - 2 * sig)
    result = np.round(result, 4)
    if isinstance(x, (int, float, np.number)):
        return float(result.item())
    else:
        return result.tolist()

import math
def compute_ball_radius(centroid: list[float], X: list[list[float]]) -> float:
    """Compute the maximum Euclidean distance between *centroid* and the
    points in *X* (i.e., the Ball-tree node radius), rounded to 4 decimals.
    """
    if not X:
        return 0.0
    max_distance = 0.0
    for point in X:
        squared_dist = sum(((p - c) ** 2 for (p, c) in zip(point, centroid)))
        distance = math.sqrt(squared_dist)
        max_distance = max(max_distance, distance)
    return round(max_distance, 4)

import numpy as np
def leaky_relu(x: np.ndarray | list | int | float, alpha: float=0.3, order: int=0) -> list:
    """Compute Leaky ReLU, its first derivative, or its second derivative.

    Args:
        x: Values to be transformed. Accepts NumPy array, list, or scalar.
        alpha: Slope used when an element is non-positive (default 0.3).
        order: 0 – return the function value; 1 – return the first derivative; 2 – return the second derivative.

    Returns:
        Python list with the same shape as *x* that contains the requested quantity.
    """
    x_array = np.array(x)
    if order not in {0, 1, 2}:
        return []
    if order == 0:
        result = np.where(x_array > 0, x_array, alpha * x_array)
    elif order == 1:
        result = np.where(x_array > 0, 1.0, alpha)
    else:
        result = np.zeros_like(x_array)
    return result.tolist()

from typing import Any, Dict
def flatten_parameters(params: Dict[str, Any]) -> Dict[str, Any]:
    """Flatten a nested parameter dictionary.

    A dot (`.`) is used to join nested keys.  The input dictionary must not be
    mutated.

    Args:
        params: A potentially nested dictionary where some values can be other
                dictionaries.

    Returns:
        A new dictionary without nested structures.
    """

    def _flatten(current_dict: Dict[str, Any], parent_key: str='') -> Dict[str, Any]:
        items = {}
        for (key, value) in current_dict.items():
            new_key = f'{parent_key}.{key}' if parent_key else key
            if isinstance(value, dict):
                items.update(_flatten(value, new_key))
            else:
                items[new_key] = value
        return items
    return _flatten(params)

import numpy as np
from typing import Callable
def unhot(function: Callable) -> Callable:
    """Decorator that converts one-hot encoded label arrays to 1-D class labels.

    If either *actual* or *predicted* is a 2-D array whose second dimension is
    larger than one, the array is assumed to be one-hot encoded and is replaced
    by its ``argmax`` along axis 1 before the wrapped *function* is executed.

    Args:
        function: A metric function accepting two NumPy arrays ``(actual,
            predicted)`` and returning a value of any type.

    Returns:
        Callable: A new function that performs the described conversion and then
        calls *function*.
    """

    def wrapper(actual: np.ndarray, predicted: np.ndarray, *args, **kwargs):

        def decode(arr: np.ndarray) -> np.ndarray:
            if arr.ndim == 2 and arr.shape[1] > 1:
                return arr.argmax(axis=1)
            return arr.reshape(-1)
        actual_decoded = decode(actual)
        predicted_decoded = decode(predicted)
        return function(actual_decoded, predicted_decoded, *args, **kwargs)
    return wrapper

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """
    if isinstance(z, list):
        if not z:
            return []
        arr = np.array(z)
    elif isinstance(z, (int, float)):
        arr = np.array(z)
    else:
        arr = z
    grad = arr * (1 - arr)
    grad_rounded = np.round(grad, 4)
    if np.isscalar(arr):
        return float(grad_rounded)
    else:
        return grad_rounded.tolist()

import numpy as np
def relu_grad(z):
    """Compute the element-wise derivative of the ReLU activation.

    The function should return 0 for every entry that is less than or equal to
    zero and 1 for every entry that is strictly greater than zero. Scalars
    must yield a single integer; arrays/lists must yield a list of identical
    shape.
    """
    z_array = np.array(z)
    grad = (z_array > 0).astype(int)
    if np.isscalar(z) or z_array.ndim == 0:
        return int(grad.item())
    return grad.tolist()

import os
from typing import List, Union
_ARCHIVE_EXTENSIONS = {'tar': {'.tar', '.tar.gz', '.tgz', '.tar.bz', '.tar.bz2', '.tbz'}, 'zip': {'.zip'}}
def extract_archive(file_path: str, path: str='.', archive_format: Union[str, List[str], None]='auto') -> bool:
    """Checks whether *file_path* belongs to an allowed archive format.

    The function MUST NOT raise any exception and MUST NOT perform real file
    extraction. It inspects the file extension and compares it with the allowed
    formats based on the archive_format parameter.

    Args:
        file_path: Full path (or name) of the archive file.
        path: Destination folder – ignored in this simplified version.
        archive_format: Format(s) allowed for extraction. See task description.

    Returns:
        True if the file name matches one of the allowed formats, False otherwise.
    """
    if archive_format is None or archive_format == []:
        return False
    if archive_format == 'auto':
        allowed_formats = {'tar', 'zip'}
    elif isinstance(archive_format, str):
        allowed_formats = {archive_format.lower()}
    else:
        allowed_formats = {fmt.lower() for fmt in archive_format}
    valid_formats = {'tar', 'zip'}
    if not allowed_formats.issubset(valid_formats):
        invalid = allowed_formats - valid_formats
        return False
    filename = os.path.basename(file_path)
    ext = os.path.splitext(filename)[1].lower()
    full_ext = os.path.splitext(os.path.splitext(filename)[0])[1].lower() + ext
    for fmt in allowed_formats:
        for allowed_ext in _ARCHIVE_EXTENSIONS[fmt]:
            if filename.lower().endswith(allowed_ext):
                return True
    return False

import numpy as np
def rmsprop_update(params: list[np.ndarray], grads: list[np.ndarray], s: list[np.ndarray] | None=None, alpha: float=0.01, beta: float=0.9, epsilon: float=1e-08) -> tuple[list[np.ndarray], list[np.ndarray]]:
    """Perform a single RMSProp optimization step."""
    if len(params) != len(grads):
        raise ValueError('params and grads must have the same length')
    if s is None or len(s) == 0:
        s = [np.zeros_like(param) for param in params]
    elif len(s) != len(params):
        raise ValueError('s must have the same length as params')
    new_params = []
    new_s = []
    for (param, grad, s_i) in zip(params, grads, s):
        updated_s = beta * s_i + (1 - beta) * grad ** 2
        param_update = alpha * grad / (np.sqrt(updated_s) + epsilon)
        new_param = np.round(param - param_update, 6)
        new_params.append(new_param)
        new_s.append(updated_s)
    return (new_params, new_s)

def tree_stats(root: 'node | None') -> tuple[int, int]:
    """Return the number of leaf nodes and the maximum depth of the tree.

    A **leaf** is defined as a node whose ``results`` attribute is not ``None``.

    Parameters
    ----------
    root : node | None
        Root of the decision tree.  If *None*, the tree is considered empty.

    Returns
    -------
    tuple[int, int]
        ``(leaf_count, max_depth)`` where
        * *leaf_count* is the total number of leaves in the tree, and
        * *max_depth* is the length of the longest path from *root* to a leaf.
    """

    def helper(node: 'node | None') -> tuple[int, int]:
        if node is None:
            return (0, 0)
        if node.results is not None:
            return (1, 1)
        (left_leaf, left_depth) = helper(node.left)
        (right_leaf, right_depth) = helper(node.right)
        max_child_depth = max(left_depth, right_depth)
        total_leaf = left_leaf + right_leaf
        total_depth = max_child_depth + 1
        return (total_leaf, total_depth)
    return helper(root)

from collections import Counter
from typing import Any, Dict, List
def build_tree(data: List[List[Any]]) -> Dict[str, Any]:
    """Build a CART decision tree using the Gini impurity criterion."""
    if not data:
        return {}
    classes = [sample[-1] for sample in data]
    if len(set(classes)) == 1:
        return {'label': classes[0]}
    (best_feature, best_value, best_gain, left_data, right_data) = find_best_split(data)
    if best_gain <= 0:
        majority_class = Counter(classes).most_common(1)[0][0]
        if len(Counter(classes)) > 1 and Counter(classes).most_common(1)[0][1] == Counter(classes).most_common(2)[1][1]:
            for sample in data:
                if sample[-1] in [c for (c, _) in Counter(classes).most_common() if _ == Counter(classes).most_common(1)[0][1]]:
                    majority_class = sample[-1]
                    break
        return {'label': majority_class}
    left_subtree = build_tree(left_data)
    right_subtree = build_tree(right_data)
    return {'feature': best_feature, 'value': best_value, 'left': left_subtree, 'right': right_subtree}
def gini_impurity(data: List[List[Any]]) -> float:
    """Calculate the Gini impurity of a dataset."""
    if not data:
        return 0
    classes = [sample[-1] for sample in data]
    class_counts = Counter(classes)
    impurity = 1.0
    for count in class_counts.values():
        probability = count / len(data)
        impurity -= probability ** 2
    return impurity
def find_best_split(data: List[List[Any]]) -> tuple[int, Any, float, List[List[Any]], List[List[Any]]]:
    """Find the best feature and value to split on."""
    best_gain = -1
    best_feature = -1
    best_value = None
    best_left = []
    best_right = []
    n_features = len(data[0]) - 1
    current_impurity = gini_impurity(data)
    for feature_idx in range(n_features):
        unique_values = set((sample[feature_idx] for sample in data))
        for value in sorted(unique_values):
            left = [sample for sample in data if sample[feature_idx] == value]
            right = [sample for sample in data if sample[feature_idx] != value]
            if not left or not right:
                continue
            weighted_impurity = len(left) / len(data) * gini_impurity(left) + len(right) / len(data) * gini_impurity(right)
            gain = current_impurity - weighted_impurity
            if gain > best_gain or (gain == best_gain and feature_idx < best_feature):
                best_gain = gain
                best_feature = feature_idx
                best_value = value
                best_left = left
                best_right = right
    return (best_feature, best_value, best_gain, best_left, best_right)

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    current = root
    while True:
        if hasattr(current, 'y'):
            return current.y
        feature_value = features[current.label]
        if task_type == 'classification':
            matched = False
            for child in current.child:
                if hasattr(child, 'x') and child.x == feature_value:
                    current = child
                    matched = True
                    break
            if not matched:
                current = current.child[1]
        elif task_type == 'regression':
            if feature_value <= current.s:
                current = current.child[0]
            else:
                current = current.child[1]

from collections import defaultdict
def naive_bayes_predict(train_data: list[tuple[str, list[str]]], keywords: set[str], test_samples: list[list[str]]) -> list[str]:
    """Build an unsmoothed Bernoulli Naive Bayes text classifier and predict labels."""
    class_counts = defaultdict(int)
    keyword_counts = defaultdict(lambda : defaultdict(int))
    for (label, tokens) in train_data:
        class_counts[label] += 1
        token_set = set(tokens)
        for word in keywords:
            if word in token_set:
                keyword_counts[label][word] += 1
    probabilities = defaultdict(dict)
    for cls in class_counts:
        total_docs = class_counts[cls]
        for word in keywords:
            counts = keyword_counts[cls].get(word, 0)
            probabilities[cls][word] = counts / total_docs if total_docs > 0 else 0
    predictions = []
    for sample in test_samples:
        sample_keywords = set(sample)
        best_class = None
        max_likelihood = -1
        for cls in class_counts:
            likelihood = 1.0
            for word in keywords:
                p_word = probabilities[cls][word]
                if word in sample_keywords:
                    likelihood *= p_word
                else:
                    likelihood *= 1 - p_word
            if likelihood > max_likelihood:
                max_likelihood = likelihood
                best_class = cls
        predictions.append(best_class)
    return predictions

import numpy as np
def minesweeper_step(ground: list[list[float | int]], flags: list[list[bool]]) -> tuple[list[tuple[int, int]], list[tuple[int, int]]]:
    """Perform one logical deduction step on a Minesweeper board."""
    rows = len(ground)
    cols = len(ground[0]) if rows > 0 else 0
    clicks = set()
    new_flags = set()
    for r in range(rows):
        for c in range(cols):
            if np.isnan(ground[r][c]) or flags[r][c]:
                continue
            value = ground[r][c]
            neighbors = []
            flagged_bombs = 0
            for dr in [-1, 0, 1]:
                for dc in [-1, 0, 1]:
                    if dr == 0 and dc == 0:
                        continue
                    (nr, nc) = (r + dr, c + dc)
                    if 0 <= nr < rows and 0 <= nc < cols:
                        neighbors.append((nr, nc))
            for (nr, nc) in neighbors:
                if flags[nr][nc]:
                    flagged_bombs += 1
            unexplored = [(nr, nc) for (nr, nc) in neighbors if not (np.isnan(ground[nr][nc]) and (not flags[nr][nc]))]
            if value == 0:
                for coord in neighbors:
                    if not flags[coord[0]][coord[1]] and np.isnan(ground[coord[0]][coord[1]]):
                        clicks.add(coord)
            elif value == 8:
                for coord in neighbors:
                    if not flags[coord[0]][coord[1]] and np.isnan(ground[coord[0]][coord[1]]):
                        new_flags.add(coord)
            elif flagged_bombs == value:
                for coord in unexplored:
                    if np.isnan(ground[coord[0]][coord[1]]) and (not flags[coord[0]][coord[1]]):
                        clicks.add(coord)
            elif len(unexplored) == value - flagged_bombs:
                for coord in unexplored:
                    if np.isnan(ground[coord[0]][coord[1]]) and (not flags[coord[0]][coord[1]]):
                        new_flags.add(coord)
    return (sorted(clicks), sorted(new_flags))

from copy import deepcopy
def deduce_mines(constraints: list[list]) -> tuple[list[str], list[str]]:
    """Simplify Minesweeper constraints to determine certainly safe cells and mined cells."""
    working_constraints = deepcopy(constraints)
    definitely_safe = set()
    definitely_mines = set()
    while True:
        changed = False
        for (vars, val) in working_constraints[:]:
            if val == 0:
                definitely_safe.update(vars)
                working_constraints.remove([vars, val])
                changed = True
            elif val == len(vars):
                definitely_mines.update(vars)
                working_constraints.remove([vars, val])
                changed = True
        if definitely_safe or definitely_mines:
            for constraint in working_constraints[:]:
                (vars, val) = constraint
                new_vars = []
                new_val = val
                for var in vars:
                    if var in definitely_mines:
                        new_val -= 1
                    elif var not in definitely_safe:
                        new_vars.append(var)
                if new_val != val or len(new_vars) != len(vars):
                    changed = True
                    if len(new_vars) == 0:
                        working_constraints.remove(constraint)
                    else:
                        constraint[0] = new_vars
                        constraint[1] = new_val
                        if new_val == 0:
                            definitely_safe.update(new_vars)
                            working_constraints.remove(constraint)
                        elif new_val == len(new_vars):
                            definitely_mines.update(new_vars)
                            working_constraints.remove(constraint)
        for (i, (vars1, val1)) in enumerate(working_constraints):
            for (j, (vars2, val2)) in enumerate(working_constraints):
                if i != j and set(vars1).issubset(set(vars2)) and (len(vars1) < len(vars2)):
                    diff_vars = [var for var in vars2 if var not in vars1]
                    new_constraint = [diff_vars, val2 - val1]
                    working_constraints.pop(j)
                    working_constraints.insert(j, new_constraint)
                    changed = True
                    break
            if changed:
                break
        unique_constraints = []
        for constraint in working_constraints:
            if constraint not in unique_constraints:
                unique_constraints.append(constraint)
        if len(unique_constraints) != len(working_constraints):
            working_constraints = unique_constraints
            changed = True
        if not changed:
            break
    return (sorted(definitely_safe), sorted(definitely_mines))

import numpy as np
def convert_maze_to_graph(maze: np.ndarray) -> dict:
    """Convert a binary maze to an adjacency-list representation."""
    (rows, cols) = maze.shape
    graph = {}
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    for r in range(rows):
        for c in range(cols):
            if maze[r, c] == 1:
                neighbors = []
                for (dr, dc) in directions:
                    (nr, nc) = (r + dr, c + dc)
                    if 0 <= nr < rows and 0 <= nc < cols and (maze[nr, nc] == 1):
                        neighbors.append((nr, nc))
                neighbors.sort()
                graph[r, c] = neighbors
    return graph
