import numpy as np
def glm_links():
    """
    Construct and return standard GLM link functions.

    Returns
    -------
    dict
        A dictionary with entries for 'identity', 'log', and 'logit' links.
        Each entry is a dictionary containing:
        - 'link': the link function g(μ)
        - 'inv_link': the inverse function g⁻¹(η)
        - 'deriv': the derivative of the link function g’(μ)
        All functions support numpy scalars and 1-D/2-D arrays via element-wise operations.
    """

    def identity_link(mu):
        return mu

    def identity_inv_link(eta):
        return eta

    def identity_deriv(mu):
        return np.ones_like(mu)

    def log_link(mu):
        return np.log(mu)

    def log_inv_link(eta):
        return np.exp(eta)

    def log_deriv(mu):
        return 1.0 / mu

    def logit_link(mu):
        mu = np.clip(mu, np.finfo(mu.dtype).eps, 1 - np.finfo(mu.dtype).eps)
        return np.log(mu / (1 - mu))

    def logit_inv_link(eta):
        return 1 / (1 + np.exp(-eta))

    def logit_deriv(mu):
        mu = np.clip(mu, np.finfo(mu.dtype).eps, 1 - np.finfo(mu.dtype).eps)
        return 1 / (mu * (1 - mu))
    return {'identity': {'link': identity_link, 'inv_link': identity_inv_link, 'deriv': identity_deriv}, 'log': {'link': log_link, 'inv_link': log_inv_link, 'deriv': log_deriv}, 'logit': {'link': logit_link, 'inv_link': logit_inv_link, 'deriv': logit_deriv}}

