from typing import List, Dict, Tuple, Hashable
def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """Return all frequent itemsets in **transactions**.

    Args:
        transactions: List where each element represents a transaction and is
                       itself a list of items (hashable Python objects).
        minsup:       Minimum support given as a fraction between 0 and 1.

    Returns:
        A dictionary that maps every frequent itemset (stored as a tuple of
        sorted items) to its absolute support count.
    """
    N = len(transactions)
    min_support_count = math.ceil(minsup * N)
    dataset = [set(transaction) for transaction in transactions]
    frequent_itemsets = {}
    counter = Counter()
    for transaction in dataset:
        counter.update(transaction)
    current_frequent = {item: count for (item, count) in counter.items() if count >= min_support_count}
    frequent_singletons = {(item,): count for (item, count) in current_frequent.items()}
    frequent_itemsets.update(frequent_singletons)
    if not frequent_singletons:
        return {}
    k = 2
    while True:
        prev_freq_items = list(current_frequent.keys())
        candidates = []
        for i in range(len(prev_freq_items)):
            for j in range(i + 1, len(prev_freq_items)):
                itemset1 = prev_freq_items[i]
                itemset2 = prev_freq_items[j]
                if isinstance(itemset1, tuple):
                    merged = tuple(sorted(set(itemset1) | set(itemset2)))
                else:
                    merged = tuple(sorted([itemset1] + [itemset2]))
                if len(merged) == k:
                    valid = True
                    for subset in combinations(merged, k - 1):
                        if tuple(sorted(subset)) not in frequent_itemsets:
                            valid = False
                            break
                    if valid and tuple(merged) not in frequent_itemsets:
                        candidates.append(merged)
        if not candidates:
            break
        candidate_counter = Counter()
        for transaction in dataset:
            for candidate in candidates:
                if set(candidate).issubset(transaction):
                    candidate_counter[tuple(sorted(candidate))] += 1
        current_frequent = {}
        for (itemset, count) in candidate_counter.items():
            if count >= min_support_count:
                frequent_itemsets[itemset] = count
                current_frequent[itemset] = count
        k += 1
    return frequent_itemsets

import numpy as np
def softplus(x, deriv: bool=False):
    """
    Compute the SoftPlus activation or its derivative (sigmoid) for input x.

    Parameters:
    - x (scalar, list, or np.ndarray): Input real values.
    - deriv (bool): If True, return the derivative (sigmoid), else return SoftPlus.

    Returns:
    - Python list or float: Element-wise SoftPlus or sigmoid values rounded to 4 decimals.
    """
    x_is_scalar = np.isscalar(x)
    x = np.asarray(x, dtype=np.float64)
    if not deriv:
        result = np.where(x > 0, x + np.log1p(np.exp(-x)), np.log1p(np.exp(x)))
    else:
        result = 1 / (1 + np.exp(-x))
    result = np.round(result, 4)
    if x_is_scalar:
        return float(result.item())
    else:
        return result.tolist()

import numpy as np
def adaboost_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_clf: int=5) -> list[int]:
    """Train AdaBoost with decision stumps and predict labels for X_test."""
    (m, n) = X_train.shape
    weights = np.ones(m) / m
    clfs = []
    alphas = []
    if n_clf < 1:
        n_clf = 1
    for _ in range(n_clf):
        min_error = float('inf')
        best_stump = {'feature': -1, 'threshold': None, 'polarity': None}
        best_predictions = None
        for feature in range(n):
            unique_vals = np.unique(X_train[:, feature])
            thresholds = (unique_vals[:-1] + unique_vals[1:]) / 2
            for threshold in thresholds:
                for polarity in [1, -1]:
                    predictions = np.ones(m)
                    if polarity == 1:
                        predictions[X_train[:, feature] <= threshold] = -1
                    else:
                        predictions[X_train[:, feature] >= threshold] = -1
                    weighted_error = np.sum(weights[y_train != predictions])
                    if weighted_error < min_error:
                        min_error = weighted_error
                        best_stump = {'feature': feature, 'threshold': threshold, 'polarity': polarity}
                        best_predictions = predictions.copy()
        epsilon = max(1e-10, min_error)
        alpha = 0.5 * np.log((1 - epsilon) / epsilon)
        weights *= np.exp(-alpha * y_train * best_predictions)
        weights /= np.sum(weights)
        clfs.append(best_stump)
        alphas.append(alpha)
    k = X_test.shape[0]
    final_predictions = np.zeros(k)
    for t in range(n_clf):
        stump = clfs[t]
        alpha = alphas[t]
        feature = stump['feature']
        threshold = stump['threshold']
        polarity = stump['polarity']
        pred = np.ones(k)
        if polarity == 1:
            pred[X_test[:, feature] <= threshold] = -1
        else:
            pred[X_test[:, feature] >= threshold] = -1
        final_predictions += alpha * pred
    return np.sign(final_predictions).astype(int).tolist()

