import math
def escape_fire_maze(grid: list[str]) -> int:
    n = len(grid)
    fire_time = [[math.inf] * n for _ in range(n)]
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    fire_queue = deque()
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_queue.append((i, j, 0))
                fire_time[i][j] = 0
    while fire_queue:
        (x, y, t) = fire_queue.popleft()
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n and (grid[nx][ny] == '.') and (fire_time[nx][ny] == math.inf):
                fire_time[nx][ny] = t + 1
                fire_queue.append((nx, ny, t + 1))
    visited = [[False] * n for _ in range(n)]
    queue = deque()
    if fire_time[0][0] <= 0:
        return -1
    queue.append((0, 0, 0))
    visited[0][0] = True
    while queue:
        (x, y, t) = queue.popleft()
        if x == n - 1 and y == n - 1:
            return t
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n and (grid[nx][ny] == '.') and (not visited[nx][ny]):
                if fire_time[nx][ny] > t + 1:
                    visited[nx][ny] = True
                    queue.append((nx, ny, t + 1))
    return -1

import numpy as np
def fit_lasso(X: np.ndarray, y: np.ndarray, lambda_param: float=1.0, max_iters: int=100, fit_intercept: bool=True) -> tuple[list[float], float]:
    """Fit Lasso (L1-regularised) linear regression using coordinate descent."""
    (m, n) = X.shape
    if fit_intercept:
        X = np.hstack((np.ones((m, 1)), X))
    else:
        X = np.hstack((np.zeros((m, 1)), X))
    w = np.zeros(n + 1)
    for _ in range(max_iters):
        if fit_intercept:
            residuals = y - X @ w
            w[0] = np.mean(residuals)
        for j in range(1, n + 1):
            x_j = X[:, j]
            w_j = w[j]
            w[j] = 0
            residuals = y - X @ w
            a = np.dot(x_j, residuals)
            if lambda_param > 0:
                tau = lambda_param * m
                if abs(a) > tau:
                    w[j] = np.sign(a) * (abs(a) - tau) / np.dot(x_j, x_j)
                else:
                    w[j] = 0
            else:
                w[j] = a / np.dot(x_j, x_j)
            w[j] = w_j
    bias = w[0]
    weights = w[1:]
    weights_list = [round(float(w), 4) for w in weights]
    bias_float = round(float(bias), 4)
    return (weights_list, bias_float)

import numpy as np
def adaboost_predict(X_train: list[list[int | float]], y_train: list[int], X_test: list[list[int | float]], n_estimators: int=10) -> list[int]:
    """AdaBoost prediction function using decision stumps."""
    X_train = np.array(X_train)
    y_train = np.array(y_train)
    X_test = np.array(X_test)
    (n_samples, n_features) = X_train.shape
    w = np.ones(n_samples) / n_samples
    y_train_sign = np.where(y_train == 0, -1, 1)
    weak_learners = []
    for _ in range(n_estimators):
        stump = find_best_stump(X_train, y_train_sign, w)
        if stump is None:
            break
        (feature_idx, threshold, polarity) = stump
        pred = predict_stump(X_train, feature_idx, threshold, polarity)
        errors = pred != y_train_sign
        error_t = np.sum(w * errors)
        if error_t == 0:
            weak_learners.append((stump, float('inf')))
            break
        alpha_t = 0.5 * np.log((1 - error_t) / (error_t + 1e-10))
        w *= np.exp(-alpha_t * y_train_sign * pred)
        w /= np.sum(w)
        weak_learners.append((stump, alpha_t))
    final_pred = predict_ensemble(X_test, weak_learners)
    return (final_pred == 1).astype(int).tolist()
def find_best_stump(X, y, w):
    """Find the decision stump with minimal weighted error."""
    (n_samples, n_features) = X.shape
    best_error = float('inf')
    best_stump = None
    for feature_idx in range(n_features):
        values = X[:, feature_idx]
        thresholds = np.unique(values)
        for threshold in thresholds:
            for polarity in [1, -1]:
                pred = predict_stump(X, feature_idx, threshold, polarity)
                errors = pred != y
                error = np.sum(w * errors)
                if error < best_error or (error == best_error and (feature_idx < best_stump[0] if best_stump else True) or (feature_idx == best_stump[0] and threshold < best_stump[1]) or (feature_idx == best_stump[0] and threshold == best_stump[1] and (polarity == 1))):
                    best_error = error
                    best_stump = (feature_idx, threshold, polarity)
    return best_stump
def predict_stump(X, feature_idx, threshold, polarity):
    """Predict using a decision stump."""
    feature_col = X[:, feature_idx]
    if polarity == 1:
        return np.where(feature_col < threshold, -1, 1)
    else:
        return np.where(feature_col >= threshold, 1, -1)
def predict_ensemble(X, weak_learners):
    """Aggregate predictions from all weak learners."""
    n_samples = X.shape[0]
    final_pred = np.zeros(n_samples)
    for (stump, alpha) in weak_learners:
        pred = predict_stump(X, *stump)
        final_pred += alpha * pred
    return np.sign(final_pred)

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def validate_vectors(x, y):
        if not isinstance(x, (list, tuple, np.ndarray)) or not isinstance(y, (list, tuple, np.ndarray)):
            return False
        x = np.array(x)
        y = np.array(y)
        if x.ndim != 1 or y.ndim != 1 or x.shape != y.shape or (len(x) == 0):
            return False
        try:
            x = x.astype(float)
            y = y.astype(float)
        except ValueError:
            return False
        return True

    def euclidean(x, y):
        return round(float(np.sqrt(np.sum((x - y) ** 2))), 4)

    def manhattan(x, y):
        return round(float(np.sum(np.abs(x - y))), 4)

    def chebyshev(x, y):
        return round(float(np.max(np.abs(x - y))), 4)

    def cosine(x, y):
        norm_x = np.linalg.norm(x)
        norm_y = np.linalg.norm(y)
        if norm_x == 0 or norm_y == 0:
            return -1
        dot_product = np.dot(x, y)
        cos_sim = dot_product / (norm_x * norm_y)
        return round(float(1 - cos_sim), 4)
    metrics = {'euclidean': euclidean, 'manhattan': manhattan, 'chebyshev': chebyshev, 'cosine': cosine}
    if name not in metrics:
        return lambda x, y: -1

    def distance_func(x, y):
        if not validate_vectors(x, y):
            return -1
        x = np.array(x).astype(float)
        y = np.array(y).astype(float)
        return metrics[name](x, y)
    return distance_func

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function follows all the standard rules including handling for busts,
    natural blackjacks, and correct hand value calculation with usable aces.

    Args:
        player: List of integers (1–10) representing the player's final hand.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def hand_value(hand: list[int]) -> int:
        """Calculate the best possible value of a hand not exceeding 21."""
        total = sum(hand)
        aces = hand.count(1)
        for _ in range(aces):
            if total + 10 <= 21:
                total += 10
        return total

    def is_natural_blackjack(hand: list[int]) -> bool:
        """Check if the hand is a natural blackjack (Ace + 10 in two cards)."""
        return len(hand) == 2 and (hand[0] == 1 and hand[1] == 10 or (hand[0] == 10 and hand[1] == 1))
    player_score = hand_value(player)
    dealer_score = hand_value(dealer)
    player_bust = player_score > 21
    dealer_bust = dealer_score > 21
    player_natural = is_natural_blackjack(player)
    dealer_natural = is_natural_blackjack(dealer)
    if player_bust:
        return -1.0
    if dealer_bust:
        return 1.0
    if player_natural and (not dealer_natural):
        return 1.5
    if player_score > dealer_score:
        return 1.0
    elif player_score < dealer_score:
        return -1.0
    else:
        return 0.0

import numpy as np
from dataclasses import dataclass
@dataclass
class Leaf:
    """A terminal node that stores a prediction value."""
    value: object
@dataclass
class Node:
    """An internal decision-tree node.

    Attributes:
        feature (int):   Index of the feature to test.
        threshold (float): Threshold that splits the data.
        left (Node | Leaf):  Sub-tree for samples with feature value < threshold.
        right (Node | Leaf): Sub-tree for samples with feature value ≥ threshold.
    """
    feature: int
    threshold: float
    left: object
    right: object
def compare_trees(tree_a, tree_b):
    """Recursively checks whether *tree_a* and *tree_b* are equivalent.

    Args:
        tree_a: Root node of the first decision tree (Node or Leaf).
        tree_b: Root node of the second decision tree (Node or Leaf).

    Returns:
        True if the two trees are equivalent, False otherwise.
    """
    if isinstance(tree_a, Leaf) and isinstance(tree_b, Leaf):
        try:
            return np.allclose(tree_a.value, tree_b.value, atol=1e-08)
        except:
            return tree_a.value == tree_b.value
    if isinstance(tree_a, Node) and isinstance(tree_b, Node):
        if tree_a.feature != tree_b.feature:
            return False
        if not np.isclose(tree_a.threshold, tree_b.threshold, atol=1e-08):
            return False
        return compare_trees(tree_a.left, tree_b.left) and compare_trees(tree_a.right, tree_b.right)
    return False

import numpy as np
def bayesian_posterior_mean(X: np.ndarray, y: np.ndarray, alpha: float=1.0, beta: float=1.0, mu=0.0, V=None, fit_intercept: bool=True) -> list[float]:
    """
    Compute the posterior mean (MAP estimate) of the coefficients in Bayesian linear regression.
    """
    X = X.copy()
    if fit_intercept:
        X = np.hstack([np.ones((X.shape[0], 1)), X])
    (N, M) = X.shape
    if np.isscalar(mu):
        mu = np.full(M, mu)
    else:
        mu = np.array(mu)
    if V is None:
        V_inv = np.eye(M)
    elif np.isscalar(V):
        V_inv = 1 / V * np.eye(M)
    elif isinstance(V, (list, tuple, np.ndarray)):
        V = np.array(V)
        if V.ndim == 1:
            V_inv = np.diag(1 / V)
        else:
            V_inv = np.linalg.inv(V)
    else:
        raise ValueError('Invalid type for V')
    XtX = X.T @ X
    V_inv_plus_XtX = V_inv + XtX
    Sigma_b = np.linalg.inv(V_inv_plus_XtX)
    Xt_y = X.T @ y
    V_inv_mu = V_inv @ mu
    mu_b = Sigma_b @ (V_inv_mu + Xt_y)
    return [round(float(x), 4) for x in mu_b]

from collections import Counter
def build_fp_tree(transactions: list[list[str]], min_support: int) -> dict:
    """Construct an FP-Tree using only nested dictionaries."""
    item_support = Counter()
    for transaction in transactions:
        item_support.update(set(transaction))
    frequent_items = {item for (item, count) in item_support.items() if count >= min_support}
    ordered_items = sorted(frequent_items, key=lambda x: (-item_support[x], x))
    item_order_index = {item: idx for (idx, item) in enumerate(ordered_items)}
    fp_tree = {'support': 0, 'children': {}}
    for transaction in transactions:
        filtered = [item for item in transaction if item in frequent_items]
        ordered_transaction = sorted(set(filtered), key=lambda x: item_order_index[x])
        current_level = fp_tree['children']
        for item in ordered_transaction:
            if item not in current_level:
                current_level[item] = {'support': 0, 'children': {}}
            current_level[item]['support'] += 1
            current_level = current_level[item]['children']
    return fp_tree

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def check_space(space: Space, is_composite: bool) -> bool:
        """Helper function to check if a space is continuous."""
        if is_composite:
            if isinstance(space, (Tuple, Dict)):
                return all((isinstance(subspace, Box) for subspace in space.spaces.values()))
            else:
                return False
        else:
            return isinstance(space, Box)
    cont_action = check_space(env.action_space, tuple_action)
    cont_obs = check_space(env.observation_space, tuple_obs)
    return (cont_action, cont_obs)

import numpy as np
def gradient_boosting_predict(updates: list[np.ndarray], learning_rate: float, regression: bool) -> np.ndarray:
    """Aggregate the outputs of Gradient Boosting trees."""
    running_pred = np.zeros_like(updates[0])
    for u in updates:
        running_pred -= learning_rate * u
    if regression:
        return np.round(running_pred, 4)
    else:
        logits = running_pred
        logits -= np.max(logits, axis=1, keepdims=True)
        exp_logits = np.exp(logits)
        probs = exp_logits / np.sum(exp_logits, axis=1, keepdims=True)
        return np.argmax(probs, axis=1)

import numpy as np
def env_stats(actions: list, observations: list) -> dict:
    """Compute statistics for collections of actions and observations."""

    def analyze_data(data):
        is_tuple = any((isinstance(x, (list, tuple, np.ndarray)) for x in data))
        first_elem = data[0]
        if isinstance(first_elem, (list, tuple, np.ndarray)):
            is_multidim = len(first_elem) > 1
            dim = len(first_elem)
        else:
            is_multidim = False
            dim = 1
        flat_data = []
        for x in data:
            if isinstance(x, (list, tuple, np.ndarray)):
                flat_data.extend(x)
            else:
                flat_data.append(x)
        is_continuous = any((isinstance(x, float) and (not x.is_integer()) for x in flat_data))
        dim_values = {}
        for x in data:
            if isinstance(x, (list, tuple, np.ndarray)):
                for (i, val) in enumerate(x):
                    if i not in dim_values:
                        dim_values[i] = set()
                    dim_values[i].add(val)
            else:
                if 0 not in dim_values:
                    dim_values[0] = set()
                dim_values[0].add(x)
        sorted_dims = sorted(dim_values.keys())
        n_per_dim = [len(dim_values[i]) for i in sorted_dims]
        ids_per_dim = [[val for val in sorted(dim_values[i])] for i in sorted_dims]
        return {'is_tuple': is_tuple, 'is_multidim': is_multidim, 'is_continuous': is_continuous, 'n_per_dim': n_per_dim, 'dim': dim, 'ids': ids_per_dim}
