import numpy as np
def gbdt_regression(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_estimators: int=10, learning_rate: float=0.1, max_depth: int=3) -> list[float]:
    """Gradient Boosting Decision Tree (GBDT) regressor.

    Parameters and return values as described in the problem.
    """

    def mse(y_true, y_pred):
        return np.mean((y_true - y_pred) ** 2)

    class Node:

        def __init__(self, feature=None, threshold=None, left=None, right=None, value=None):
            self.feature = feature
            self.threshold = threshold
            self.left = left
            self.right = right
            self.value = value

    class DecisionTreeRegressor:

        def __init__(self, max_depth):
            self.max_depth = max_depth
            self.root = None

        def fit(self, X, y):
            self.root = self._grow_tree(X, y)

        def _grow_tree(self, X, y, depth=0):
            (n_samples, n_features) = X.shape
            if depth >= self.max_depth or len(np.unique(y)) == 1:
                return Node(value=np.mean(y))
            best_mse = mse(y, np.mean(y))
            best_split = None
            for feat_idx in range(n_features):
                thresholds = np.unique(X[:, feat_idx])
                for t in thresholds:
                    left_mask = X[:, feat_idx] <= t
                    right_mask = X[:, feat_idx] > t
                    if np.sum(left_mask) == 0 or np.sum(right_mask) == 0:
                        continue
                    y_left = y[left_mask]
                    y_right = y[right_mask]
                    current_mse = (len(y_left) * mse(y_left, np.mean(y_left)) + len(y_right) * mse(y_right, np.mean(y_right))) / n_samples
                    if current_mse < best_mse:
                        best_mse = current_mse
                        best_split = (feat_idx, t)
            if best_split is None:
                return Node(value=np.mean(y))
            (feat_idx, threshold) = best_split
            left_mask = X[:, feat_idx] <= threshold
            right_mask = X[:, feat_idx] > threshold
            left = self._grow_tree(X[left_mask], y[left_mask], depth + 1)
            right = self._grow_tree(X[right_mask], y[right_mask], depth + 1)
            return Node(feature=feat_idx, threshold=threshold, left=left, right=right)

        def predict(self, X):
            return np.array([self._predict_sample(x, self.root) for x in X])

        def _predict_sample(self, x, node):
            if node.value is not None:
                return node.value
            if x[node.feature] <= node.threshold:
                return self._predict_sample(x, node.left)
            else:
                return self._predict_sample(x, node.right)
    predictions = np.full_like(y_train, fill_value=np.mean(y_train), dtype=float)
    trees = []
    for _ in range(n_estimators):
        residuals = y_train - predictions
        tree = DecisionTreeRegressor(max_depth=max_depth)
        tree.fit(X_train, residuals)
        trees.append(tree)
        predictions += learning_rate * tree.predict(X_train)
    final_predictions = np.full(shape=len(X_test), fill_value=np.mean(y_train), dtype=float)
    for tree in trees:
        final_predictions += learning_rate * tree.predict(X_test)
    return [round(float(p), 4) for p in final_predictions]

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in {0, 1, 2}:
        return -1
    input_type = type(x)
    x = np.array(x, dtype=float)
    sig = 1 / (1 + np.exp(-x))
    if order == 0:
        result = sig
    elif order == 1:
        result = sig * (1 - sig)
    elif order == 2:
        result = sig * (1 - sig) * (1 - 2 * sig)
    result = np.round(result, 4)
    if isinstance(x, (int, float, np.number)):
        return float(result.item())
    else:
        return result.tolist()

import numpy as np
from typing import Callable
def unhot(function: Callable) -> Callable:
    """Decorator that converts one-hot encoded label arrays to 1-D class labels.

    If either *actual* or *predicted* is a 2-D array whose second dimension is
    larger than one, the array is assumed to be one-hot encoded and is replaced
    by its ``argmax`` along axis 1 before the wrapped *function* is executed.

    Args:
        function: A metric function accepting two NumPy arrays ``(actual,
            predicted)`` and returning a value of any type.

    Returns:
        Callable: A new function that performs the described conversion and then
        calls *function*.
    """

    def wrapper(actual: np.ndarray, predicted: np.ndarray, *args, **kwargs):

        def decode(arr: np.ndarray) -> np.ndarray:
            if arr.ndim == 2 and arr.shape[1] > 1:
                return arr.argmax(axis=1)
            return arr.reshape(-1)
        actual_decoded = decode(actual)
        predicted_decoded = decode(predicted)
        return function(actual_decoded, predicted_decoded, *args, **kwargs)
    return wrapper

