import math
def mel_filterbank(N: int, n_filters: int=20, fs: int=44000, min_freq: int=0, max_freq: int | None=None, normalize: bool=True) -> list[list[float]]:
    """Build a Mel filterbank transformation matrix.

    The returned matrix has *n_filters* rows and *N//2 + 1* columns.  Each row
    is a triangular filter defined on the Mel scale.  See the detailed task
    description for the exact algorithm that has to be implemented.
    """
    if max_freq is None:
        max_freq = fs / 2

    def hz_to_mel(f):
        return 2595 * math.log10(1 + f / 700)

    def mel_to_hz(m):
        return 700 * (10 ** (m / 2595) - 1)
    mel_min = hz_to_mel(min_freq)
    mel_max = hz_to_mel(max_freq)
    mels = np.linspace(mel_min, mel_max, n_filters + 2)
    freqs = [mel_to_hz(m) for m in mels]
    num_bins = N // 2 + 1
    bin_freqs = [k * fs / N for k in range(num_bins)]
    filterbank = []
    for i in range(n_filters):
        f_left = freqs[i]
        f_center = freqs[i + 1]
        f_right = freqs[i + 2]
        weights = []
        for k in range(num_bins):
            f_k = bin_freqs[k]
            if f_k <= f_left:
                left_slope = 0.0
            else:
                left_slope = (f_k - f_left) / (f_center - f_left)
            if f_k >= f_right:
                right_slope = 0.0
            else:
                right_slope = (f_right - f_k) / (f_right - f_center)
            weight = min(left_slope, right_slope)
            weights.append(weight)
        if normalize:
            denominator = f_right - f_left
            if denominator > 0:
                weights = [2 * w / denominator for w in weights]
        filterbank.append([round(w, 4) for w in weights])
    return filterbank

from collections import Counter
def build_fp_tree(transactions: list[list[str]], min_support: int) -> dict:
    """Construct an FP-Tree using only nested dictionaries."""
    item_counts = Counter()
    for transaction in transactions:
        unique_items = set(transaction)
        item_counts.update(unique_items)
    frequent_items = {item for (item, count) in item_counts.items() if count >= min_support}
    ordered_items = sorted(frequent_items, key=lambda x: (-item_counts[x], x))
    root = {'support': 0, 'children': {}}
    for transaction in transactions:
        filtered_items = [item for item in transaction if item in frequent_items]
        unique_items = list(set(filtered_items))
        ordered_items_in_transaction = sorted(unique_items, key=lambda x: ordered_items.index(x))
        current_node = root
        for item in ordered_items_in_transaction:
            if item not in current_node['children']:
                current_node['children'][item] = {'support': 0, 'children': {}}
            current_node = current_node['children'][item]
            current_node['support'] += 1
    return root

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def is_continuous_space(space: Space) -> bool:
        if isinstance(space, Box):
            return True
        elif isinstance(space, Tuple) or isinstance(space, Dict):
            if isinstance(space, Tuple):
                return all((is_continuous_space(subspace) for subspace in space.spaces))
            else:
                return all((is_continuous_space(subspace) for subspace in space.spaces.values()))
        else:
            return False
    if tuple_action:
        cont_action = is_continuous_space(env.action_space)
    else:
        cont_action = isinstance(env.action_space, Box)
    if tuple_obs:
        cont_obs = is_continuous_space(env.observation_space)
    else:
        cont_obs = isinstance(env.observation_space, Box)
    return (cont_action, cont_obs)

