def gbdt_regression(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_estimators: int=10, learning_rate: float=0.1, max_depth: int=3) -> list[float]:
    """Gradient Boosting Decision Tree (GBDT) regressor.

    Parameters
    ----------
    X_train : np.ndarray
        2-D array with shape (n_samples, n_features) containing the training
        features.
    y_train : np.ndarray
        1-D array of length n_samples containing the training targets.
    X_test : np.ndarray
        2-D array with shape (m_samples, n_features) containing the test
        features to predict.
    n_estimators : int, default=10
        Number of boosting iterations.
    learning_rate : float, default=0.1
        Shrinkage factor applied to each tree’s prediction.
    max_depth : int, default=3
        Maximum depth of every individual regression tree.

    Returns
    -------
    list[float]
        Predictions for every sample in *X_test*, rounded to 4 decimal places.
    """
    y_pred = np.full_like(y_train, np.mean(y_train))
    for _ in range(n_estimators):
        residuals = y_train - y_pred
        tree = build_tree(X_train, residuals, max_depth)
        y_pred += learning_rate * predict_tree(X_train, tree)
    test_predictions = predict_tree(X_test, tree)
    return [round(pred, 4) for pred in test_predictions]
def build_tree(X: np.ndarray, y: np.ndarray, max_depth: int) -> dict:
    """Build a regression tree using CART algorithm."""

    def split_dataset(X, y, feature_idx, threshold):
        left_mask = X[:, feature_idx] < threshold
        right_mask = ~left_mask
        return (X[left_mask], y[left_mask], X[right_mask], y[right_mask])

    def calculate_mse(y):
        return np.mean((y - np.mean(y)) ** 2)

    def find_best_split(X, y):
        best_gain = 0
        best_feature = None
        best_threshold = None
        for feature_idx in range(X.shape[1]):
            unique_vals = np.unique(X[:, feature_idx])
            for threshold in unique_vals:
                (X_left, y_left, X_right, y_right) = split_dataset(X, y, feature_idx, threshold)
                if len(y_left) == 0 or len(y_right) == 0:
                    continue
                gain = calculate_mse(y) - (len(y_left) / len(y) * calculate_mse(y_left) + len(y_right) / len(y) * calculate_mse(y_right))
                if gain > best_gain:
                    best_gain = gain
                    best_feature = feature_idx
                    best_threshold = threshold
        return (best_feature, best_threshold, best_gain)

    def build_node(X, y, depth):
        if depth == max_depth or len(np.unique(y)) == 1:
            return {'value': np.mean(y)}
        (feature_idx, threshold, gain) = find_best_split(X, y)
        if gain == 0:
            return {'value': np.mean(y)}
        (X_left, y_left, X_right, y_right) = split_dataset(X, y, feature_idx, threshold)
        left_child = build_node(X_left, y_left, depth + 1)
        right_child = build_node(X_right, y_right, depth + 1)
        return {'feature': feature_idx, 'threshold': threshold, 'left': left_child, 'right': right_child}
    return build_node(X, y, 0)
def predict_tree(X: np.ndarray, tree: dict) -> np.ndarray:
    """Predict using a trained regression tree."""

    def predict_sample(x, node):
        if 'value' in node:
            return node['value']
        if x[node['feature']] < node['threshold']:
            return predict_sample(x, node['left'])
        else:
            return predict_sample(x, node['right'])
    return np.array([predict_sample(x, tree) for x in X])

