def root_mean_squared_log_error(actual: list[float], predicted: list[float]) -> float:
    """Compute the Root Mean Squared Logarithmic Error (RMSLE) between actual and predicted values.

    Args:
        actual: List of non-negative float values (ground truth).
        predicted: List of non-negative float values (model predictions).

    Returns:
        RMSLE value rounded to 4 decimal places, or -1 if inputs are invalid.
    """
    if len(actual) != len(predicted):
        return -1
    for val in actual + predicted:
        if val < 0:
            return -1
    try:
        log_actual = np.log(1 + np.array(actual))
        log_predicted = np.log(1 + np.array(predicted))
        squared_diffs = (log_predicted - log_actual) ** 2
        mean_squared_log_error = np.mean(squared_diffs)
        rmsle = np.sqrt(mean_squared_log_error)
        return round(rmsle, 4)
    except Exception:
        return -1

