def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in {0, 1, 2}:
        return -1
    x_np = np.asarray(x)
    sigma = 1 / (1 + np.exp(-x_np))
    if order == 0:
        result = sigma
    elif order == 1:
        result = sigma * (1 - sigma)
    elif order == 2:
        result = sigma * (1 - sigma) * (1 - 2 * sigma)
    result = np.round(result, 4)
    if np.isscalar(x):
        return float(result)
    else:
        return result.tolist()

import itertools
def apriori_frequent_itemsets(transactions: list[list[int]], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """
    from collections import defaultdict
    item_counts = defaultdict(int)
    for transaction in transactions:
        for item in transaction:
            item_counts[item] += 1
    total_transactions = len(transactions)
    min_support_count = int(min_sup * total_transactions)
    frequent_1_itemsets = []
    for (item, count) in item_counts.items():
        if count >= min_support_count:
            frequent_1_itemsets.append(tuple([item]))
    frequent_1_itemsets.sort()
    frequent_itemsets = frequent_1_itemsets
    k = 2
    while True:
        candidates = set()
        for itemset in frequent_itemsets:
            if len(itemset) == k - 1:
                for item in item_counts:
                    if item > itemset[-1]:
                        new_candidate = tuple(sorted(itemset + (item,)))
                        candidates.add(new_candidate)
        pruned_candidates = []
        for candidate in candidates:
            for subset in itertools.combinations(candidate, k - 1):
                if tuple(sorted(subset)) not in frequent_itemsets:
                    break
            else:
                pruned_candidates.append(candidate)
        if not pruned_candidates:
            break
        candidate_counts = defaultdict(int)
        for transaction in transactions:
            for candidate in pruned_candidates:
                if set(candidate).issubset(transaction):
                    candidate_counts[candidate] += 1
        new_frequent_itemsets = []
        for candidate in pruned_candidates:
            if candidate_counts[candidate] >= min_support_count:
                new_frequent_itemsets.append(candidate)
        new_frequent_itemsets.sort()
        frequent_itemsets.extend(new_frequent_itemsets)
        k += 1
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    if not X or not all((isinstance(row, list) for row in X)):
        return -1
    C = len(X[0])
    if Y is None:
        Y = X
    if not Y or not all((isinstance(row, list) for row in Y)):
        return -1
    if any((len(row) != C for row in Y)):
        return -1
    if sigma is None:
        sigma = np.sqrt(C / 2)
    elif isinstance(sigma, float):
        if sigma <= 0:
            return -1
    elif isinstance(sigma, list):
        if len(sigma) != C:
            return -1
        if any((s <= 0 for s in sigma)):
            return -1
    else:
        return -1
    X_np = np.array(X)
    Y_np = np.array(Y)
    diff = X_np[:, np.newaxis, :] - Y_np[np.newaxis, :, :]
    squared_diff = diff ** 2
    if isinstance(sigma, float):
        scaled_squared_diff = squared_diff / sigma ** 2
    else:
        scaled_squared_diff = squared_diff / (np.array(sigma) ** 2)[np.newaxis, np.newaxis, :]
    sum_squared_diff = np.sum(scaled_squared_diff, axis=2)
    kernel_matrix = np.exp(-0.5 * sum_squared_diff)
    return np.round(kernel_matrix, 4).tolist()

